OSM.Survey.set("forceSlideContentView", true);

this.hideSection = function(obj_sectionname) {

    var objCollectionQuestions = obj_sectionname.get('childNodes');

    objCollectionQuestions.forEach(function(questionRef) {

        var objInstanceType = questionRef.get('instanceType');

        switch(objInstanceType.toString())
        {
            case "RM.OSM.Survey.Question":
                questionRef.hide(); 

                var objDataType = questionRef.get('dataType');

                switch(objDataType.get('datatype'))
                {
                    case 1: //Category
                        questionRef.enableAnswers();    
                        break;
                    case 2: //Numeric
                    case 3: //Text
                    case 4: //DateTime
                        questionRef.enableComment();
                        break;
                }
                break;
            case "RM.OSM.Survey.Section":
                OSM.Survey.hideSection(questionRef);
                break;
            case "RM.OSM.Survey.Loop":
                questionRef.hide();
                break;
        }
    });
};

this.hideSectionWithQuestions = function(obj_sectionname, arr_questionnames) {

    var objCollectionQuestions = obj_sectionname.get('childNodes');

    objCollectionQuestions.forEach(function(questionRef) {

        var objInstanceType = questionRef.get('instanceType');

        switch(objInstanceType.toString())
        {
            case "RM.OSM.Survey.Question":
                var questionName = questionRef.get('objectName');

                if(arr_questionnames.indexOf(questionName) !== -1)
                {
                    var objDataType = questionRef.get('dataType');

                    switch(objDataType.get('datatype'))
                    {
                        case 1: //Category
                            questionRef.enableAnswers();    
                            break;
                        case 2: //Numeric
                        case 3: //Text
                        case 4: //DateTime
                            questionRef.enableComment();
                            break;
                    }

                    questionRef.hide();
                }
                break;
            case "RM.OSM.Survey.Section":
                //Check hide all questions or hide specified questions in section
                var sectionName = questionRef.get('objectName');

                if(arr_questionnames.indexOf(sectionName) !== -1)
                {
                    OSM.Survey.hideSection(questionRef);
                }
                else
                {
                    var check = false;

                    var objTempCollectionQuestions = questionRef.get('childNodes');

                    objTempCollectionQuestions.forEach(function(tempquestionRef) {

                        var tempquestionName = tempquestionRef.get('objectName');

                        if(arr_questionnames.indexOf(tempquestionName) !== -1)
                        {
                            check = true;
                        }
                    });

                    if(check)
                    {
                        OSM.Survey.hideSectionWithQuestions(questionRef, arr_questionnames);
                    }
                }
                break;
            case "RM.OSM.Survey.Loop":
                var loopName = questionRef.get('objectName');

                if(arr_questionnames.indexOf(loopName) !== -1)
                {
                    questionRef.hide();
                }
                break;
        }
    });
};

this.showSection = function(obj_sectionname) {

    var objCollectionQuestions = obj_sectionname.get('childNodes');

    objCollectionQuestions.forEach(function(questionRef) {

        var objInstanceType = questionRef.get('instanceType');

        switch(objInstanceType.toString())
        {
            case "RM.OSM.Survey.Question":
                questionRef.show(); 

                var objDataType = questionRef.get('dataType');

                switch(objDataType.get('datatype'))
                {
                    case 1: //Category
                        questionRef.enableAnswers();    
                        break;
                    case 2: //Numeric
                    case 3: //Text
                    case 4: //DateTime
                        questionRef.enableComment();
                        break;
                }
                break;
            case "RM.OSM.Survey.Section":
                OSM.Survey.showSection(questionRef);
                break;
            case "RM.OSM.Survey.Loop":
                questionRef.show(); 
                break;
        }
    });
};

this.showSectionWithQuestions = function(obj_sectionname, arr_questionnames, isenabled) {

    var objCollectionQuestions = obj_sectionname.get('childNodes');

    objCollectionQuestions.forEach(function(questionRef) {

        var objInstanceType = questionRef.get('instanceType');

        switch(objInstanceType.toString())
        {
            case "RM.OSM.Survey.Question":
                var questionName = questionRef.get('objectName');

                if(arr_questionnames.indexOf(questionName) !== -1)
                {
                    questionRef.show();

                    var objDataType = questionRef.get('dataType');

                    switch(objDataType.get('datatype'))
                    {
                        case 1: //Category
                            if(!isenabled)
                            {
                                questionRef.disableAnswers(questionRef.getAnswers(), false);    
                            }
                            else
                            {
                                questionRef.enableAnswers(questionRef.getAnswers());
                            }
                            break;
                        case 2: //Numeric
                        case 3: //Text
                        case 4: //DateTime
                            if(!isenabled)
                            {
                                questionRef.disableComment();    
                            }
                            else
                            {
                                questionRef.enableComment();
                            }
                            break;
                    }   
                }
                break;
            case "RM.OSM.Survey.Section":
                var sectionName = questionRef.get('objectName');

                if(arr_questionnames.indexOf(sectionName) !== -1)
                {
                    OSM.Survey.showSection(questionRef);
                }
                else
                {
                    var check = false;

                    var objTempCollectionQuestions = questionRef.get('childNodes');

                    objTempCollectionQuestions.forEach(function(tempquestionRef) {

                        var tempquestionName = tempquestionRef.get('objectName');

                        if(arr_questionnames.indexOf(tempquestionName) !== -1)
                        {
                            check = true;
                        }
                    });

                    if(check)
                    {
                        OSM.Survey.showSectionWithQuestions(questionRef, arr_questionnames);
                    }
                }
                break;
            case "RM.OSM.Survey.Loop":
                var loopName = questionRef.get('objectName');

                if(arr_questionnames.indexOf(loopName) !== -1)
                {
                    questionRef.show();
                }
                break;
        }
    });
};

this.hideQuestions = function(arr_questions) {

    for(var i = 0; i < arr_questions.length; i++)
    {
        this.getTop().getElementsByName(arr_questions[i], this, false)[0].value.hide();
    }
};

this.showQuestions = function(arr_questions) {

    for(var i = 0; i < arr_questions.length; i++)
    {
        this.getTop().getElementsByName(arr_questions[i], this, false)[0].value.show();
    }
};

this.setOrderByItems = function(obj_question, arr_listcatresps){

    var objInstanceType = obj_question.get('instanceType');

    switch(objInstanceType.toString())
    {
        case "RM.OSM.Survey.Question":
            OSM.Survey.initializeQuestion(obj_question, arr_listcatresps)
            break;
        case "RM.OSM.Survey.Loop":
            obj_question.filterIterations(arr_listcatresps);
            break;
    }

    var obj_collection = obj_question.get('answerSet').get('answers');

    for(var i = 0; i < arr_listcatresps.length; i++)
    {
        obj_collection.moveToPosition(arr_listcatresps[i], i);
    }
}

this.setOrderConstantsByIndex = function(obj_questionname, order_constant, isallcategories, arr_listitems) {

    var arrOrderNumber = [];
    var arrListCats = [];
    var arrListOthersOrIsExclusives = [];

    if(isallcategories)
    {
        arrListOthersOrIsExclusives = OSM.Survey.definedCategoryOthersOrIsExclusive(obj_questionname);    

        arrListCats = OSM.Survey.definedCategories(obj_questionname);     

        if(arrListOthersOrIsExclusives.length > 0)
        {
            arrListCats = OSM.Survey.differenceCategoriesWith(obj_questionname, arrListOthersOrIsExclusives);
        }
    }
    else
    {
        for(var i = 0; i < arr_listitems.length; i++)
        {
            arrListCats.push(arr_listitems[i]);
        }
    }

    switch(order_constant.toUpperCase())
    {
        case "ROTATION":
            arrOrderNumber = OSM.Survey.getArrayRotationNumber(0, arrListCats.length - 1);
            break;
        case "RANDOM":
            arrOrderNumber = OSM.Survey.getArrayRandomNumber(0, arrListCats.length - 1);
            break;
        case "DEFAULT":
            for(var i = 0; i < arrListCats.length; i++)
            {
                arrOrderNumber.push(i.valueOf());
            }
            break;
    }

    var arrarrListCatOrders = [];

    for(var i = 0; i < arrOrderNumber.length; i++)
    {
        arrarrListCatOrders.push(arrListCats[arrOrderNumber[i]]);
    }

    if(arrListOthersOrIsExclusives.length > 0)
    {
        for(var i = 0; i < arrListOthersOrIsExclusives.length; i++)
        {
            arrarrListCatOrders.push(arrListOthersOrIsExclusives[i]);
        }
    }

    obj_questionname.get('answerSet').get('answers').orderByItems(arrarrListCatOrders);
}

OSM.Survey.ranSectionElements = function(obj_sectionname, arrQresList) {

    RM.Utils.reorder(obj_sectionname.get('objectName'), arrQresList.join(","));
}

this.getMyDate = function(currentdate, strformat_date){
    /*
     * function getMyDate(string format_date)
     * hàm trả về Date được format theo formar_date (string)
     * formar_date:
     * + YYYY-MM-DD
     * + YYYY/MM/DD
     * + DD.MM.YYYY
     * + DD/MM/YYYY
     */
    var now = currentdate;

    var year = now.getFullYear();
    var month = now.getMonth() + 1; //January is 0
    var day = now.getDate();

    if(month.toString().length == 1)
    {
        month = '0' + month.toString();
    }
    if(day.toString().length == 1)
    {
        day = '0' + day.toString();
    }

    var str_date_updated = "";

    switch(strformat_date.toUpperCase())
    {
        case "YYYY-MM-DD":
            str_date_updated = year.toString() + '-' + month.toString() + '-' + day.toString();
            break;
        case "YYYY/MM/DD":
            str_date_updated = year.toString() + '/' + month.toString() + '/' + day.toString();
            break;
        case "DD.MM.YYYY":
            str_date_updated = day.toString() + '.' + month.toString() + '.' + year.toString();
            break;
        case "DD/MM/YYYY":
            str_date_updated = day.toString() + '/' + month.toString() + '/' + year.toString();
            break;
    }

    return str_date_updated;
};

this.getMyTime = function(currentdate) {

    var now     = currentdate; 

    var hour    = now.getHours();
    var minute  = now.getMinutes();
    var second  = now.getSeconds(); 

    if(hour.toString().length == 1) {
        hour = '0'+hour;
    }
    if(minute.toString().length == 1) {
        minute = '0'+ minute;
    }
    if(second.toString().length == 1) {
        second = '0'+ second;
    }

    return hour + ':' + minute + ':' + second;
};

this.addDays = function(_current_date, days){
    var date_current = new Date(_current_date);

    date_current.setDate(date_current.getDate() + days);

    return date_current;
}

this.getMyRandomNumber = function(minVal, maxVal)
{
    var rnd = 0;
    rnd = Math.floor(Math.random() * (maxVal - minVal + 1)) + minVal;
    return rnd;
}

this.getArrayNumber = function(minVal, maxVal) {

    var arrNumber = [];

    for(var i = minVal; i <= maxVal; i++)
    {
        arrNumber.push('_' + i.toString());
    }

    return arrNumber;
};

this.getArrayRotationNumber = function(minVal, maxVal) {

    var arrOrderNumber = new Array(maxVal + 1);

    var rnd = OSM.Survey.getMyRandomNumber(minVal, maxVal);

    for(var i = 0; i < maxVal + 1; i++)
    {
        arrOrderNumber[i] = rnd;
        rnd += 1;

        if(rnd > maxVal)
        {
            rnd = minVal;
        }
    }

    return arrOrderNumber;
};

this.getArrayRandomNumber = function(minVal, maxVal) {

    var arrRandomNumber = new Array(maxVal + 1);

    var iterationIndex = 0;

    do 
    {
        var rnd = OSM.Survey.getMyRandomNumber(minVal, maxVal);    

        if(arrRandomNumber.indexOf(rnd) === -1)
        {
            arrRandomNumber[iterationIndex] = rnd;
            iterationIndex += 1;
        }
    }
    while(iterationIndex < maxVal + 1);

    return arrRandomNumber;
};

this.getArrayRandomCategories = function(arr_listcats, number_of_categories){

    var arrRandomCats = new Array(number_of_categories);

    var iterationIndex = 0;

    do 
    {
        var rnd = OSM.Survey.getMyRandomNumber(1, arr_listcats.length) - 1;    

        if(arrRandomCats.indexOf(arr_listcats[rnd]) === -1)
        {
            arrRandomCats[iterationIndex] = arr_listcats[rnd];
            iterationIndex += 1;
        }
    }
    while(iterationIndex < number_of_categories);

    return arrRandomCats;
};

this.getLabelCategories = function(obj_questionname, arr_listcats) {

    var lbl_cats = "";

    var arr_ListCats = OSM.Survey.convertArray(arr_listcats.toString()); 

    for(var i = 0; i < arr_ListCats.length; i++)
    {
        obj_questionname.get("answerSet").get('answers').forEach(function(answer) {

            var answerName = answer.get('objectName');

            if(arr_ListCats[i] === answerName)
            {
                if(obj_questionname[answerName].get('otherField') != null)
                {
                    lbl_cats = lbl_cats + obj_questionname[answerName].getComment() + ", ";
                }
                else
                {
                    lbl_cats = lbl_cats + obj_questionname[answerName].get('text').getText() + ", ";
                }
            }
        });
    }

    return lbl_cats.substring(0, lbl_cats.length - 2);
};

this.getImageNames = function(obj_questionname) {

    var objAttachmentsManager = OSM.Survey.get('attachmentsManager');

    var objAttachments = objAttachmentsManager.getAttachmentsForQuestion(obj_questionname);

    var index = 0;
    var strImageNames = "";

    objAttachments.forEach(function(attachment) {

        var id = attachment.get('id');
        var uniqueID = attachment.get('uniqueID');
        var attachmentName = attachment.get('internalIDFull');
        var fileName = attachment.get('fileName');
        var filePath = attachment.get('filePath');
        var originalName = attachment.get('originalName');
        var downloadPath = attachment.get('downloadPath');

        if((index + 1) === objAttachments.count())
        {
            strImageNames = strImageNames + originalName;
        }
        else
        {
            strImageNames = strImageNames + originalName + "#";
        }

        index += 1;
    });

    return strImageNames;
};

this.getMonthOfBirth = function(date_birthofday, date_of_interview){

    var year_birthofday = date_birthofday.getFullYear();
    var month_birthofday = date_birthofday.getMonth() + 1;
    var day_birthofday = date_birthofday.getDate();

    var year_dateofinterview = date_of_interview.getFullYear();
    var month_dateofinterview = date_of_interview.getMonth() + 1;
    var day_dateofinterview = date_of_interview.getDate();

    var number_of_years = year_dateofinterview - year_birthofday;

    var number_of_months = (month_dateofinterview - month_birthofday) + number_of_years * 12;

    //alert("Birth of Date: " + year_birthofday + " - " + month_birthofday + " - " + day_birthofday);
    //alert("Date of interview: " + year_dateofinterview + " - " + month_dateofinterview + " - " + day_dateofinterview);

    if(day_dateofinterview >= day_birthofday)
    {
        number_of_months = number_of_months;
    }
    else
    {
        number_of_months = number_of_months - 1;
    }

    //if(number_of_months == 72 && day_dateofinterview > day_birthofday)
    //{
    //    number_of_months = 73
    //}

    return number_of_months;
};

this.setRandomLoopForBackCheck = function(objLoop, number_of_items, arr_qres_text, arr_qres_cats){

    var arrCatsListRandom = [];
    arrCatsListRandom = OSM.Survey.orderArray(OSM.Survey.definedCategories(objLoop), "RANDOM", number_of_items);

    for(var i = 0; i < number_of_items; i++)
    {
        var iterations = objLoop.get('childNodes');

        iterations.forEach(function(iterator, key){

            var iterationName = iterator.get('objectName');
            var iteration = objLoop.getIteration(iterationName);

            if(iterationName == arr_listitems[i])
            {
                var questionTextRef = this.getTop().getElementsByName(arr_qres_text[i], this, false)[0].value;
                questionTextRef.setComment(objLoop[iterationName].get('text').getText());

                var questionCatRef = this.getTop().getElementsByName(arr_qres_cats[i], this, false)[0].value;

                var objCollectionQuestions = iteration.get('childNodes');

                objCollectionQuestions.forEach(function(qreRef){

                    var qreName = qreRef.get('objectName');

                    if(qreName.toLowerCase().indexOf('codes') != -1)
                    {
                        questionCatRef.setAnswers(qreRef.getAnswers());
                    }
                });

                return;
            }
        });     
    }
};

this.orderArray = function(arr_listitems, order_constant, number_of_items) {

    var arrOrderNumber = [];
    var arrCatsList = [];

    switch(order_constant.toUpperCase())
    {
        case "ROTATION":
            arrOrderNumber = OSM.Survey.getArrayRotationNumber(0, arr_listitems.length - 1);

            for(var i = 0; i < arrOrderNumber.length; i++)
            {
                arrCatsList.push(arr_listitems[arrOrderNumber[i]]);
            }
            break;
        case "RANDOM":
            arrOrderNumber = OSM.Survey.getArrayRandomNumber(0, arr_listitems.length - 1);

            for(var i = 0; i < arrOrderNumber.length; i++)
            {
                arrCatsList.push(arr_listitems[arrOrderNumber[i]]);
            }
            break;
        case "DEFAULT":
            arrCatsList = arr_listitems;
            break;
    }

    var arrCatsListFinal = [];

    for(var i = 0; i < number_of_items; i++)
    {
        arrCatsListFinal[i] = arrCatsList[i];    
    }

    return arrCatsListFinal;
};

this.convertArray = function(list_categories) {

    var strListCats = list_categories.toString();

    var arrListCats = [];

    var str_cat = "";

    for(var i = 0; i < strListCats.length; i++)
    {
        if(strListCats[i].toString() !== ',')
        {
            str_cat = str_cat + strListCats[i].toString();
        }

        if(strListCats[i].toString() === ',' || i === strListCats.length - 1)
        {
            arrListCats.push(str_cat);
            str_cat = "";   
        }
    }

    return arrListCats;
};

this.recodeByStep = function(arr_listcats, arr_separators, step) {

    var check = true;

    var arrListCats = OSM.Survey.convertArray(arr_listcats);

    var arrRecodeListCats = [];

    for(var i = 0; i < arrListCats.length; i++)
    {
        var intCode = arrListCats[i].toString();

        for(var j = 0; j < arr_separators.length; j++)
        {
            intCode = intCode.replace(arr_separators[j],"");
        }

        var intCode = parseInt(intCode) + parseInt(step);

        if(intCode <= 0)
        {
            check = false;
        }

        arrRecodeListCats.push("_" + intCode.toString());
    }

    if(check)
    {
        return arrRecodeListCats;    
    }
    else
    {
        return arrListCats;
    }
};

this.isNumeric = function(number) {

    var result = true;

    if(number === '')
    {
        result = false;
    }
    else
    {
        result = !isNaN(number);
    }

    return result;
};

this.trimAll = function(str) {

    var stringNew = "";

    var stringTrim = str.trim();

    for(var i = 0; i < stringTrim.length; i++)
    {
        if(stringTrim.charAt(i) !== ' ' || (stringTrim.charAt(i) === ' ' && stringTrim.charAt(i + 1) !== ' '))
        {
            stringNew = stringNew + stringTrim.charAt(i); 
        }
    }

    return stringNew;
};

this.datediff = function(starttime, endtime, interval){
    var result = 0;
    var years = 0;

    switch(interval.toUpperCase())
    {
        case "MS":
            result = endtime - starttime;
            break;
            //return miliseconds
        case "S":
            result = Math.floor((endtime - starttime) / 1000);
            break;
            //return seconds
        case "N":
            result = Math.floor(((endtime - starttime) / 1000) / 60);
            break;
            //return minutes
        case "H":
            result = Math.floor((((endtime - starttime) / 1000) / 60) / 60);
            break;
            //return hours
        case "D":
            hour = Math.floor((((endtime - starttime) / 1000) / 60) / 60);

            result = Math.floor(hour / 24);
            break;
            //return number of days
        case "M":
            years = endtime.getFullYear() - starttime.getFullYear();

            if(years === 0)
            {
                months = (endtime.getMonth() + 1) - (starttime.getMonth() + 1);
            }
            else
            {
                months = (12 - (starttime.getMonth() + 1)) + (endtime.getMonth() + 1);

                months = months > 11 ? (months - 12) + years * 12 : months + (years - 1) * 12;    
            }

            result = months;
            break;
            //return number of months
    }

    return result;
};

this.checkCurrentDay = function(daycheck)
{
    var result = true;

    var starttime = new Date(OSM.Survey._STARTDATE.getComment() + "T" + OSM.Survey._STARTTIME.getComment());
    var day_check = new Date(daycheck + 'T00:00:00');

    if(OSM.Survey.datediff(day_check, starttime, "D") < 0)
    {
        result = false;
    }

    return result;
};

this.spendDate = function(str_startdate, str_enddate, str_starttime, str_endtime) {

    var totaldate = 0;

    var starttime = new Date(str_startdate);
    var endtime = new Date(str_enddate);

    totaldate = OSM.Survey.datediff(starttime, endtime, "d");

    var str_spandate = "", str_spandate_top = "", str_spandate_bottom = "";

    switch(totaldate)
    {
        case 0:
            str_spandate = "D00 - " + OSM.Survey.spendTime(str_starttime, str_endtime);
            break;
        default:
            str_spandate_bottom = OSM.Survey.spendTime(str_starttime, "23:60:60");
            str_spandate_top = OSM.Survey.spendTime("00:00:00", str_endtime);

            str_spandate = OSM.Survey.sumSpendDate(str_spandate_bottom, str_spandate_top, totaldate);

            break;
    }

    return str_spandate;
};

this.sumSpendDate = function(str_spantime_a, str_spantime_b, totaldate) {

    var arr_spantime_a = str_spantime_a.split(':');
    var arr_spantime_b = str_spantime_b.split(':');

    var seconds = parseInt(arr_spantime_a[2].toString()) + parseInt(arr_spantime_b[2].toString());

    var minutes = parseInt(arr_spantime_a[1].toString()) + parseInt(arr_spantime_b[1].toString());

    var hours = parseInt(arr_spantime_a[0].toString()) + parseInt(arr_spantime_b[0].toString());

    var dates = totaldate - 1;

    if(seconds >= 60)
    {
        minutes += 1;
        seconds = 0;
    }

    if(minutes >= 60)
    {
        hours += 1;
        minutes = 0;
    }

    if(hours >= 24)
    {
        dates += 1;
        hours = 0;
    }

    var str_dates = (dates.toString().length == 2 ? "D" : "D0") + dates.toString();

    var str_hours = hours.toString().length == 2 ? hours.toString() : "0" + hours.toString();
    var str_minutes = minutes.toString().length == 2 ? minutes.toString() : "0" + minutes.toString()
    var str_seconds = seconds.toString().length == 2 ? seconds.toString() : "0" + seconds.toString()

    var str_spantime = str_dates + " - " + str_hours + ":" + str_minutes + ":" + str_seconds;

    return str_spantime;
};

this.spendTime = function(str_starttime, str_endtime) {

    var arr_starttime = str_starttime.split(':');
    var arr_endtime = str_endtime.split(':');

    var hours = parseInt(arr_endtime[0].toString()) - parseInt(arr_starttime[0].toString());

    var minutes = -1;

    if(parseInt(arr_starttime[1].toString()) > parseInt(arr_endtime[1].toString()))
    {
        minutes = 60 - parseInt(arr_starttime[1].toString()) + parseInt(arr_endtime[1].toString())
        hours -= 1;
    }
    else
    {
        minutes = parseInt(arr_endtime[1].toString()) - parseInt(arr_starttime[1].toString());
    }

    var seconds = -1;

    if(parseInt(arr_starttime[2].toString()) > parseInt(arr_endtime[2].toString()))
    {
        seconds = 60 - parseInt(arr_starttime[2].toString()) + parseInt(arr_endtime[2].toString())
        minutes -= 1;
    }
    else
    {
        seconds = parseInt(arr_endtime[2].toString()) - parseInt(arr_starttime[2].toString());
    }

    var str_hours = hours.toString().length == 2 ? hours.toString() : "0" + hours.toString();
    var str_minutes = minutes.toString().length == 2 ? minutes.toString() : "0" + minutes.toString()
    var str_seconds = seconds.toString().length == 2 ? seconds.toString() : "0" + seconds.toString()

    var str_spantime = str_hours + ":" + str_minutes + ":" + str_seconds;

    return str_spantime;
};

this.totalTime = function(str_spantime){

    str_spantime = str_spantime.replace(" - ","-");

    var arr_spantime = str_spantime.split('-');

    var totaltime = parseInt(arr_spantime[0].toString().replace("D",""));

    var arr_times = arr_spantime[1].toString().split(':');

    totaltime += parseInt(arr_times[0].toString());
    totaltime = (totaltime * 60) + parseInt(arr_times[1].toString());
    totaltime = (totaltime * 60) + parseInt(arr_times[2].toString());

    return totaltime;
};

this.initializeQuestion = function(obj_questionname, arr_listcats) {

    var arrListCats = OSM.Survey.convertArray(arr_listcats.toString());

    obj_questionname.get('answerSet').get('answers').forEach(function(answer) {

        var pos = -1;

        answer.hide();

        if(arrListCats.indexOf(answer.get('objectName')) !== -1)
        {
            if(!answer.get('isDisplayAsHeader'))
            {
                if(obj_questionname[answer.get('objectName')].get('answerSetReference').length > 0){

                    obj_questionname[obj_questionname[answer.get('objectName')].get('answerSetReference')].show();
                    obj_questionname[obj_questionname[answer.get('objectName')].get('answerSetReference')].enable();
                }

                pos = obj_questionname[answer.get('objectName')].get('groupID');

                if(pos != '')
                {
                    var answerHeader = obj_questionname.get('answerSet').getAnswerByPosition(pos);

                    if(answerHeader.get('text').getText().length == 0)
                    {
                        pos = obj_questionname[answerHeader.get('objectName')].get('groupID');

                        if(pos != '')
                        {
                            var answerHeader = obj_questionname.get('answerSet').getAnswerByPosition(pos);

                            if(answerHeader.get('isDisplayAsHeader'))
                            {
                                answerHeader.show();
                                answerHeader.enable();
                            }
                        }
                    }
                    else
                    {
                        if(answerHeader.get('isDisplayAsHeader'))
                        {
                            answerHeader.show();
                            answerHeader.enable();
                        }
                    }
                }

                answer.show();
                answer.enable();  
            }
        }
    }); 

    var resps = [];

    if(OSM.Survey.countCategories(obj_questionname.getAnswers()) !== 0)
    {
        resps = OSM.Survey.convertArray(obj_questionname.getAnswers());
    }

    obj_questionname.clearAnswers(OSM.Survey.definedCategories(obj_questionname));

    if(arrListCats.length === 1)
    {
        obj_questionname.setAnswers(arrListCats);
    }
    else
    {
        if(resps.length > 0)
        {
            var resps_fixed = [];

            for(var i = 0; i < resps.length; i++)
            {
                if(arrListCats.indexOf(resps[i]) === -1)
                {
                    resps_fixed.push(resps[i]);
                }
            }

            if(resps_fixed.length > 0)
            {
                obj_questionname.clearAnswers(resps_fixed);    
            }
        }
    }
};

this.initializeQuestions = function(arr_questionnames) {

    for(var i = 0; i < arr_questionnames.length; i++)
    {
        var obj_question = this.getTop().getElementsByName(arr_questionnames[i], this, false)[0].value;

        OSM.Survey.initializeQuestion(obj_question, obj_question.getAnswers());
    }
};

this.initializeQuestionsWithCatResps = function(arr_questionnames, arr_listcatresps) {

    for(var i = 0; i < arr_questionnames.length; i++)
    {
        var obj_question = this.getTop().getElementsByName(arr_questionnames[i], this, false)[0].value;

        OSM.Survey.initializeQuestion(obj_question, arr_listcatresps);
    }
};

this.initializeQuestionComment = function(obj_questionname, str_value){

    obj_questionname.enableComment();

    var objDataType = obj_questionname.get('dataType');

    switch(parseInt(objDataType.get('datatype')))
    {
        case 2:
            obj_questionname.setComment(parseFloat(str_value));
            break;
        case 3:
            obj_questionname.setComment(str_value.toString());
            break;
        case 4:
            obj_questionname.setComment(new Date(str_value));
            break;
    }

    obj_questionname.disableComment();
};

this.showDialogQuestion = function(obj_questionname, arr_listcats_selected) {

    var arrListCatsSelected = OSM.Survey.convertArray(arr_listcats_selected.toString());

    obj_questionname.clearAnswers(OSM.Survey.definedCategories(obj_questionname));

    obj_questionname.get('answerSet').get('answers').forEach(function(answer) {

        var answerName = answer.get('objectName');

        answer.hide();

        if(arrListCatsSelected.indexOf(answerName) !== -1)
        {
            if(obj_questionname[answerName].get('answerSetReference').length > 0){

                obj_questionname[obj_questionname[answerName].get('answerSetReference')].show();
                obj_questionname[obj_questionname[answerName].get('answerSetReference')].enable();
            }

            pos = obj_questionname[answerName].get('groupID');

            if(pos != '')
            {
                var answerHeader = obj_questionname.get('answerSet').getAnswerByPosition(pos);

                answerHeader.show();
                answerHeader.enable();
            }

            answer.show();
            answer.enable();
        }
    });

    obj_questionname.setAnswers(arrListCatsSelected);
    obj_questionname.disableAnswers(arrListCatsSelected, false);
};

this.showDialogQuestionHasOtherCats = function(obj_question_show, obj_question_resp) {

    var arr_othercats_selected = OSM.Survey.definedCategoryOthers(obj_question_resp);

    var obj_others = {};

    var arr_cats_selected = OSM.Survey.convertArray(obj_question_resp.getAnswers());

    for(var i = 0; i < arr_othercats_selected.length; i++)
    {
        if(arr_cats_selected.indexOf(arr_othercats_selected[i]) !== -1)
        {
            obj_others[arr_othercats_selected[i]] = obj_question_resp.get('answerSet')[arr_othercats_selected[i]].getComment();
        }
    }

    obj_question_show.clearAnswers(OSM.Survey.definedCategories(obj_question_show));

    obj_question_show.get('answerSet').get('answers').forEach(function(answer) {

        var answerName = answer.get('objectName');

        answer.hide();

        if(arr_cats_selected.indexOf(answerName) !== -1)
        {
            pos = obj_question_show[answerName].get('groupID');

            if(pos != '')
            {
                var answerHeader = OSM.Survey.getAnswerByPosition(obj_question_show, pos);

                answerHeader.show();
                answerHeader.enable();
            }

            answer.show();
            answer.enable();
        }
    });

    obj_question_show.setAnswers(arr_cats_selected);

    if(arr_othercats_selected.length > 0)
    {
        for(var key in obj_others)
        {
            obj_question_show.get('answerSet')[key].setComment(obj_others[key]);    
        }
    }

    obj_question_show.disableAnswers(arr_cats_selected, false);
};

this.definedCategories = function(obj_questionname) {

    var arrListCats = [];

    obj_questionname.get('answerSet').get('answers').forEach(function(answer) {

        if(!answer.get('isDisplayAsHeader'))
        {
            var answerName = answer.get('objectName');

            arrListCats.push(answerName);
        }
    });

    return arrListCats;
};

this.definedCategoryOthers = function(obj_questionname) {

    var arrListCats = [];

    obj_questionname.get('answerSet').get('answers').forEach(function(answer) {

        if(answer.get('otherField') != null)
        {
            var answerName = answer.get('objectName');

            arrListCats.push(answerName);
        }
    });

    return arrListCats;
};

this.definedCategoryOthersOrIsExclusive = function(obj_questionname) {

   var arrListCats = [];

    obj_questionname.get('answerSet').get('answers').forEach(function(answer) {

        if(answer.get('isOtherSpecify') || answer.get('isExclusive'))
        {
            var answerName = answer.get('objectName');

            arrListCats.push(answerName);
        }
    });

    return arrListCats;
};

this.unionCategories = function() {

    var arrUnionListCats = [];
    var arrListCats = [];

    for(var i in arguments) {

        arrListCats = OSM.Survey.convertArray(arguments[i].toString());

        for(var j = 0; j < arrListCats.length; j++)
        {
            if(arrUnionListCats.indexOf(arrListCats[j]) == -1)
            {
                arrUnionListCats.push(arrListCats[j]);
            }
        }
    };

    return arrUnionListCats;
};

this.intersectionCategories = function() {

    var arrIntersectionListCats = [];

    var arrListCats_1 = OSM.Survey.convertArray(arguments[0].toString());
    var arrListCats_2 = OSM.Survey.convertArray(arguments[1].toString());

    for(var i = 0; i < arrListCats_1.length; i++)
    {
        if(arrListCats_2.indexOf(arrListCats_1[i]) != -1)
        {
            arrIntersectionListCats.push(arrListCats_1[i]);
        }
    }

    if(arguments.length > 1)
    {
        for(var i in arguments)
        {
            if(i > 1)
            {
                arrListCats_1 = arrIntersectionListCats;
                arrListCats_2 = OSM.Survey.convertArray(arguments[i].toString());

                arrIntersectionListCats = new Array();

                for(var i = 0; i < arrListCats_1.length; i++)
                {
                    if(arrListCats_2.indexOf(arrListCats_1[i]) != -1)
                    {
                        arrIntersectionListCats.push(arrListCats_1[i]);
                    }
                }
            }
        }
    }

    return arrIntersectionListCats;
};

this.intersectionCategoriesWith = function() {

    var arrListCatsEnable = [];

    for(var i in arguments)
    {
        if(i > 0)
        {
            arrListCatsEnable = OSM.Survey.unionCategories(arrListCatsEnable, OSM.Survey.convertArray(arguments[i].toString()));
        }
    }

    //Question
    var objQuestion = arguments[0];

    //Declare a Array to store categories of Question that it have in the list of categories will be enable
    var arrListCats = [];

    objQuestion.get('answerSet').get('answers').forEach(function(answer) {

        var answerName = answer.get('objectName');

        if(arrListCatsEnable.indexOf(answerName) !== -1)
        {
            arrListCats.push(answerName);
        }
    });

    return arrListCats;
};

this.intersectionArrListCatsWith = function() {

    //List of categories will be removed
    var arrListCatsEnable = [];

    for(var i in arguments) {
        if(i > 0)
        {
            arrListCatsEnable = OSM.Survey.unionCategories(arrListCatsEnable, OSM.Survey.convertArray(arguments[i].toString()));
        }
    };

    //Main Array
    var arrMain = OSM.Survey.convertArray(arguments[0].toString());

    //Declare a Array to store categories of Question that it don't have the list of categories will be removed 
    var arrListCats = [];

    for(var i = 0; i < arrMain.length; i++)
    {
        if(arrListCatsEnable.indexOf(arrMain[i].toString()) !== -1)
        {
            arrListCats.push(arrMain[i].toString());
        }
    }

    return arrListCats;
};

this.differenceCategories = function() {

    var arrDifferenceListCats = [];

    var arrListCats_1 = OSM.Survey.convertArray(arguments[0].toString());
    var arrListCats_2 = OSM.Survey.convertArray(arguments[1].toString());

    for(var i = 0; i < arrListCats_1.length; i++)
    {
        if(arrListCats_2.indexOf(arrListCats_1[i]) == -1)
        {
            arrDifferenceListCats.push(arrListCats_1[i]);
        }
    }

    if(arguments.length > 1)
    {
        for(var i in arguments)
        {
            if(i > 1)
            {
                arrListCats_1 = arrDifferenceListCats;
                arrListCats_2 = OSM.Survey.convertArray(arguments[i].toString());

                arrDifferenceListCats = new Array();

                for(var i = 0; i < arrListCats_1.length; i++)
                {
                    if(arrListCats_2.indexOf(arrListCats_1[i]) == -1)
                    {
                        arrDifferenceListCats.push(arrListCats_1[i]);
                    }
                }
            }
        }
    }

    return arrDifferenceListCats;
};

