this.countCategories = function() {

    var arrListCats = [];

    for(var i in arguments) {

        arrListCats = OSM.Survey.unionCategories(arrListCats, OSM.Survey.convertArray(arguments[i].toString()));
    };

    arrListCats = OSM.Survey.convertArray(arrListCats); 

    return arrListCats.length;
};

this.getListQresInSection = function(obj_sectionname) {

    var arrListQres = [];

    var objCollectionQuestions = obj_sectionname.get('childNodes');

    objCollectionQuestions.forEach(function(questionRef) {

        var questionName = questionRef.get('objectName');

        arrListQres.push(questionName);
    });

    return arrListQres;
};

this.getTotalMeasure = function() {

    var totalScore = 0.0;

    var objInstanceType = arguments[0].get('instanceType');

    switch(objInstanceType.toString())
    {
        case "RM.OSM.Survey.Question":
            if(arguments[0].isAnswered())
            {
                var arrRespQre = OSM.Survey.convertArray(arguments[0].getAnswers());

                for(var i = 0; i < arrRespQre.length; i++)
                {
                    totalScore += parseFloat(arguments[0][arrRespQre[i].toString()].get('measure'));
                }
            }
            break;
    }

    return totalScore;
};

this.getArrayByMeasure = function() {

    var arrCatsResp = [];

    var objInstanceType = arguments[0].get('instanceType');

    switch(objInstanceType.toString())
    {
        case "RM.OSM.Survey.Question":
            var arrListCatsOfQre = new Array();
            arrListCatsOfQre = OSM.Survey.definedCategories(arguments[0]);

            var arrListCatsOfMeasure = new Array();
            arrListCatsOfMeasure = OSM.Survey.convertArray(arguments[1]);

            for(var i = 0; i < arrListCatsOfQre.length; i++)
            {
                var cat_name = arguments[0][arrListCatsOfQre[i].toString()].get('objectName');
                var cat_measure = arguments[0][arrListCatsOfQre[i].toString()].get('measure');

                if(arrListCatsOfMeasure.indexOf("_" + cat_measure.toString()) != -1)
                {
                    if(arrCatsResp.indexOf(cat_name) == -1)
                    {
                        arrCatsResp.push(cat_name)
                    }
                }    
            }
            break;
    }

    return arrCatsResp;
};

this.getSumOfLoop = function(obj_loop, str_objectname) {

    var sum = 0;
    var iterations = obj_loop.get('childNodes');

    iterations.forEach(function(iterator, key) {

        var iterationName = iterator.get('objectName');
        var iteration = obj_loop.getIteration(iterationName);

        var objCollectionQuestions = iteration.get('childNodes');

        objCollectionQuestions.forEach(function(questionRef) {

            if(questionRef.getComment().length > 0)
            {
                var questionName = questionRef.get('objectName');

                if(questionName === str_objectname)
                {
                    sum += parseFloat(questionRef.getComment()); 
                }
            }
        });
    });

    return sum;
};

