};

this.getTable = function(title_name, arr_column_names, obj_contents){

    var width_of_column = Math.round(100 / arr_column_names.length);

    var number_of_balance = 100 - (width_of_column * arr_column_names.length);

    var str_table = "<table border = 1 style='width:100%;text-align:center'>";

    var str_tr = "", str_th = "", str_td = "";

    //Số lượng columns có trong 1 bảng
    var number_of_columns = arr_column_names.length + 1;
    var width_of_column_current = 0;

    //Title của bảng
    if(title_name.length > 0)
    {
        str_tr = "<tr>";

        str_th = "<th colspan = '" + number_of_columns.toString() + "' style='text-align:center;padding-top:5px;padding-bottom:5px'>" + title_name + "</th>";

        str_tr += str_th + "</tr>"
    }

    str_table += str_tr;

    //ColumnNames of bảng
    str_tr = "<tr>";
    str_th = "";

    for(var i = 0; i < arr_column_names.length; i++)
    {
        if(i == arr_column_names.length - 1)
        {
            width_of_column_current = width_of_column + number_of_balance;
        }
        else
        {
            width_of_column_current = width_of_column;
        }

        str_th += "<th style='width:" + width_of_column_current.toString() + "%;text-align:center;padding-top:5px;padding-bottom:5px'>" + arr_column_names[i].toString() + "</th>";
    }

    str_tr += str_th + "</tr>";

    str_table += str_tr;

    //Nội dung cua bảng
    //var element = {}; element[key] = value
    for(var key in obj_contents){

        var content = obj_contents[key];

        str_tr = "<tr>";
        str_td = "";

        width_of_column_current = 0;

        for(var prop in content){

            if(prop == arr_column_names.length - 1)
            {
                width_of_column_current = width_of_column + number_of_balance;
            }
            else
            {
                width_of_column_current = width_of_column;
            }

            str_td += "<td style='width:" + width_of_column_current.toString() + "%;text-align:left;padding-top:5px;padding-bottom:5px;border:1px solid white'>" + content[prop].toString() + "</td>";    
        }

        str_tr += str_td + "</tr>";

        str_table += str_tr;
    }

    str_table += "</table>";

    return str_table;
};

OSM.Survey.precisionRound = function(number, precision){

    var factor = Math.pow(10, precision);

    return Math.round(number * factor) / factor;
}

OSM.Survey.getTextFromCategories = function (obj_question)
{
    var arr_listcats = OSM.Survey.convertArray(obj_question.getAnswers());

    var str = "";

    for (var i = 0; i < arr_listcats.length; i++)
    {
        var k = arr_listcats[i]; //Object : key
        var v = ""; //Object : value

        if(obj_question.get('answerSet')[answer_name].get('isOtherSpecify')) 
        {
            v = obj_question.get('answerSet')[answer_name].getComment();
        }
        else
        {
            v = obj_question.get('answerSet')[answer_name].get('text').getText();
        }

        str += (str.length == 0 ? "" : ", ") + k + ":" + v;
    }

    return "{" + str + "}";
};

OSM.Survey.valCheckCharacterString = function(event) {

    var regExp = "[0-9]";
    var checkRegExp = new RegExp(regExp);

    if(checkRegExp.test(event.sourceElement.getComment())) {

        return {
            message: "Vui lòng nhập chữ!..",
            status: false
        };
    }

    return {
        status: true  
    };
};

