OSM.Survey.valCheckPhoneNumber = function(event){

    OSM.NotificationSystem.removeAllNotifications();

    if(OSM.Survey._ResPhone.isAnswerSelected(['_2']) && OSM.Survey._ResCellPhone.isAnswerSelected(['_2']))
    {
        return{
            status: false,
            message: "Đáp viên bắt buộc phải có ít nhất 1 số điện thoại."
        };
    }
    else
    {
        if(event.sourceElement.isAnswerSelected(['_1']))
        {
            var phone = event.sourceElement[event.sourceElement.getAnswers()].getComment();
            var province_id = OSM.Survey._ResProvinces.get('answerSet')[OSM.Survey._ResProvinces.getAnswers()].get('objectName');

            var strRegExp = "";
            var str_message = "";

            switch(event.sourceElement.get('objectName'))
            {
                case "_ResPhone":
                    //strRegExp = "^[0-9]{6,10}$";
                    if(OSM.Survey._ResProvinces.isAnswerSelected(['_1','_2'])){
                        strRegExp = "0" + areaCodes[province_id]['area_code'] + "[0-9]{8}$"; 

                        str_message = "Nhập mã vùng của " + areaCodes[province_id]['name'] + " (0" + areaCodes[province_id]['area_code'] + ")" + " trước số điện thoại. Cách nhập: 0 + 2X + XXXXXXXX (8 chữ số)"
                    } else {
                        strRegExp = "0" + areaCodes[province_id]['area_code'] + "[0-9]{7}$";

                        str_message = "Nhập mã vùng của " + areaCodes[province_id]['name'] + " (0" + areaCodes[province_id]['area_code'] + ")" + " trước số điện thoại. Cách nhập: 0 + 2XX + XXXXXXX (7 chữ số)"
                    }
                    break;
                case "_ResCellPhone":
                    strRegExp = "0((3[2-9]|5[2,6,8,9]|7[0,6-9]|8[1-9]|9[0-9])|(12[0-9]|16[2-9]|18[6,8]|199))[0-9]{7}$";
                    str_message = "Số điện thoại không đúng.";
                    break;
            }

            var objRegExp = new RegExp(strRegExp);

            if(!objRegExp.test(phone))
            {
                return{
                    status: false,
                    message: str_message
                };
            }
        }
    }

    return{
        status: true
    };
};

OSM.Survey.valCheckTotal = function(event) {

    var total = 0;

    OSM.NotificationSystem.removeAllNotifications();

    var iterations = event.sourceElement.parent().parent().get('childNodes');

    iterations.forEach(function(iterator, key) {

        if(iterator.get('isVisible'))
        {
            var iterationName = iterator.get('objectName');
            var iteration = event.sourceElement.parent().parent().getIteration(iterationName);

            var objCollectionQuestions = iteration.get('childNodes');

            objCollectionQuestions.forEach(function(questionRef) {

                total = total + parseInt(questionRef.getComment());    
            })
        }
    });

    if(total !== 10)
    {
        return{
            status: false,
            message: "Total = 10"
        };
    }
    else
    {
        return{
            status: true
        };
    }
};

OSM.Survey.valCheckTotalPercent = function(event) {

    var total = 0;

    OSM.NotificationSystem.removeAllNotifications();

    var iterations = event.sourceElement.parent().parent().get('childNodes');

    iterations.forEach(function(iterator, key) {

        if(iterator.get('isVisible'))
        {
            var iterationName = iterator.get('objectName');
            var iteration = event.sourceElement.parent().parent().getIteration(iterationName);

            var objCollectionQuestions = iteration.get('childNodes');

            objCollectionQuestions.forEach(function(questionRef) {

                total = total + parseInt(questionRef.getComment());    
            })
        }
    });

    if(total !== 100)
    {
        return{
            status: false,
            message: "Total = 100%"
        };
    }
    else
    {
        return{
            status: true
        };
    }
};

OSM.Survey.valCheckCurrentDay = function(event){

    if(OSM.Survey.checkCurrentDay(event.sourceElement.getComment()))
    {
        return{
            status: true
        };
    }
    else
    {
        return{
            status: false,
            message: "Ngày " + event.sourceElement.getComment() + " không hợp lệ!.."
        }
    }
};

OSM.Survey.valCheckServiceBanking = function(event){

    var arr_1 = new Array();
    arr_1 = OSM.Survey.convertArray(OSM.Survey._ServiceBanking_1.getAnswers());

    var arr_2 = new Array();
    arr_2 = OSM.Survey.convertArray(event.sourceElement.getAnswers());

    var arrGroups = new Array();

    for(var i = 0; i < arr_2.length; i++)
    {
        var group_id = "_" + event.sourceElement[arr_2[i].toString()].get('measure');

        if(arr_1.indexOf(group_id) != -1 && arrGroups.indexOf(group_id) == -1)
        {
            arrGroups.push(group_id);
        }
    }

    if(arr_1.length == arrGroups.length)
    {
        return{
            status:true
        };
    }
    else
    {
        return{
            status: false,
            message: "Kiểm tra mỗi gói dịch vụ cần chọn ít nhất 1 code!.."
        };
    }
};


OSM.Survey.valCheckCC3 = function(event){
    var result = true;
    var messagestring = "";

    var catsListCC3 = OSM.Survey.ExpressionParser.execute("_CC3.GetDefinedAnswers()");

    //alert(catsListCC3);

    var total_categories = OSM.Survey.ExpressionParser.execute("_CC3.GetDefinedAnswers()").split(',').length;

    //alert(total_categories);

    switch(total_categories)
    {
        case 6:
            if(event.sourceElement.isAnswerSelected(['_1']))
            {
                if(OSM.Survey.flag_CC3_validation === true)
                {
                    OSM.Survey.flag_CC3_validation = false;
                    event.sourceElement.validate();
                }
            }
            else
            {
                messagestring = "Vui lòng kiểm tra lại nếu đáp viên không chọn code 1 - Điện!..";   
            }

            return{
                status: true
            };
            break;
        case 7:
            var arrCatsSelected = [];

            event.sourceElement.get('answerSet').get('answers').forEach(function(answer) {
                switch(answer.get('objectName'))
                {
                    case '_1':
                        if(!event.sourceElement.isAnswerSelected(['_1']))
                        {
                            messagestring = "Vui lòng kiểm tra lại nếu đáp viên không chọn code 1 - Điện!.."; 
                        }
                        else
                        {
                            arrCatsSelected.push(answer.get('objectName'));  
                        }
                        break;
                    case '_6':
                        if(!event.sourceElement.isAnswerSelected(['_6']))
                        {
                            messagestring = "Vui lòng kiểm tra lại nếu đáp viên không chọn code 1 - Điện!.."; // new fix
                        }
                        else
                        {
                            arrCatsSelected.push(answer.get('objectName'));  
                        }
                        break;
                    case '_7':
                        if(!event.sourceElement.isAnswerSelected(['_6']))
                        {
                            if(!event.sourceElement.isAnswerSelected(['_7']))
                            {
                                messagestring = "Vui lòng kiểm tra lại nếu đáp viên không chọn code 6/7 - Toilet!.."; 
                            }
                            else
                            {
                                arrCatsSelected.push(answer.get('objectName'));  
                            }
                        }

                        break;
                }


            });

            if(arrCatsSelected.length === 2)
            {
                if(OSM.Survey.flag_CC3_validation === true)
                {
                    OSM.Survey.flag_CC3_validation = false;
                    event.sourceElement.validate();
                }

                return{
                    status: true
                };
            }

            break;
    }

    OSM.Survey.flag_CC3_validation === true;

    return{
        status: false,
        message: messagestring
    };
};

OSM.Survey.valCheckCC4 = function(event) {

    if(OSM.Survey._ResPhone.isAnswerSelected(['_1']) && !event.sourceElement.isAnswerSelected(['_18']))
    {
        return {
            status: false,
            message: "Phần thông tin đáp viên có nhập số điện thoại cố định thì code 18 phải được chọn ở CC4 !.."
        };    
    }

    return {
        status: true  
    };
};

OSM.Survey.valCheckCC5 = function(event) {

    console.log('asdsda');
    OSM.NotificationSystem.removeAllNotifications();

    if(OSM.Survey._ResCellPhone.isAnswerSelected(['_1']) && parseInt(OSM.Survey._CC5.getIteration('_13')._CC5_Codes.getComment()) === 0 && parseInt(OSM.Survey._CC5.getIteration('_14')._CC5_Codes.getComment()) === 0)
    {
        return {
            status: false,
            message: "Phần thông tin đáp viên có nhập số điện thoại di động thì code 13 hoặc 14 số lượng phải > 0 !.."
        };
    }

    return {
        status: true  
    };
};

OSM.Survey.valCheckwithR6 = function(event){

    var iteration = event.sourceElement.parent();
    var q_check = undefined;
    var q_name = "";

    switch(event.sourceElement.get('objectName'))
    {
        case '_R6a':
            q_check = iteration._R6.getIteration('_1')._Codes;
            q_name = "R6_1";
            break;
        case '_R6b':
            q_check = iteration._R6.getIteration('_2')._Codes;
            q_name = "R6_2";
            break;
        case '_R6c':
            q_check = iteration._R6.getIteration('_3')._Codes;
            q_name = "R6_3";
            break;
    }

    if(q_check.isAnswerSelected(['_1','_2']) && event.sourceElement.isAnswerSelected(['_3']))
    {
        return {
            status: false,
            message: q_name + " = 1/2, thì " + event.sourceElement.get('objectName') + " khác code 3"
        };
    }

    if(q_check.isAnswerSelected(['_6','_7']) && event.sourceElement.isAnswerSelected(['_1','_5']))
    {
        return {
            status: false,
            message: q_name + " = 6/7, thì " + event.sourceElement.get('objectName') + " khác code 1/5"
        };
    }
};

OSM.Survey.valcheckmax3 = function(event){

    if(OSM.Survey.countCategories(event.sourceElement.getAnswers()) > 3)
    {
        return {
            status: false,
            message: "Chỉ được chọn tối đa 3 nhận định."
        };
    }
};

