var startInterviewTime = new Date();

var prevstartdate = OSM.Survey._STARTDATE.getComment();

if(prevstartdate === '')
{
    OSM.Survey._STARTDATE.setComment(OSM.Survey.getMyDate(startInterviewTime, "YYYY-MM-DD"));
    OSM.Survey._STARTDATE.disableComment();

    OSM.Survey._Interview_Year.setComment(startInterviewTime.getFullYear());
}

var prevstarttime = OSM.Survey._STARTTIME.getComment();

if(prevstarttime === '')
{
    OSM.Survey._STARTTIME.setComment(OSM.Survey.getMyTime(startInterviewTime));
    OSM.Survey._STARTTIME.disableComment();
}

