                            <svg class="svgSignatureContainer" xmlns="http://www.w3.org/2000/svg" width="{{size.canvasWidth}}" height="{{size.canvasHeight}}">\
                                <line x1="{{size.linePoints.start[0]}}" y1="{{size.linePoints.start[1]}}" x2="{{size.linePoints.end[0]}}" y2="{{size.linePoints.end[1]}}" stroke="#A6A6A6" stroke-width="1"/>\
                                <path id="signPath_{{uniqueID}}" stroke="#2F2F2F" stroke-width="2" fill="none" pointer-events="none" d="{{path}}"></path>\
                            </svg>\
                            <br/>\
                            <div class="{{buttonClass}}" style="width: {{size.canvasWidth}}px;" {{userEventDown}}="RM.Map.getElementById(\'{{uniqueID}}\').clearComment(RM.Utils.eventOriginType.User)">Clear signature</div>\
                        </li>\
                    </ul>\
                </div>\
            {{/if}}\
        {{/if}}\
        {{#if renderShell}}\
            </li>\
        {{/if}}\
    ',
    "controller": function controller(renderShellOnly) {
        var survey = this.getTop();
        var renderShell = renderShellOnly ? true : !survey.get("jitRenderingEnabled");
        if (renderShell) {
            var onEventHandler = function onEventHandler(event) {
                var uniqueID = event.sourceElement.get("uniqueID");
                switch (event.property.name) {
                    case "isVisible":
                        RM.OSM.Renderers.handleQuestionVisibility(event);
                        break;
                    case "value":
                        RM.OSM.Renderers.signatureProperties[uniqueID].signPathString = RM.OSM.Renderers.signatureFunctions.fromOSMtoSVG(event.property.currentValue);
                        if (event.property.currentValue === "") {
                            RM.OSM.Renderers.signatureFunctions.clearSignature(uniqueID);
                        } else {
                            RM.OSM.Renderers.signatureFunctions.setSignature(uniqueID);
                        }
                        break;
                    case "isDisplayed":
                    case "isCompleted":
                    case "setDepressed":
                    case "isChecked":
                        break;
                    case "readOnly":
                        var parent = document.getElementById("liSignatureContainer_" + uniqueID);
                        if (event.property.currentValue) {
                            parent.style.opacity = 0.3;
                            parent.children[1].className = "clrSignButton_disabled";
                        } else {
                            if (event.sourceElement.get("isEnabled") !== false) {
                                parent.style.opacity = 1;
                                parent.children[1].className = "clrSignButton";
                            }
                        }
                        break;
                    case "isEnabled":
                        var parent = document.getElementById("liSignatureContainer_" + uniqueID);
                        if (event.property.currentValue) {
                            if (!event.sourceElement.get("readOnly")) {
                                parent.style.opacity = 1;
                                parent.children[1].className = "clrSignButton";
                            }
                        } else {
                            parent.style.opacity = 0.3;
                            parent.children[1].className = "clrSignButton_disabled";
                        }
                        break;
                    default:
                        console.error("DefaultSignatureTemplate: Unknown property name!", event.property.name);
                        break;
                }
            };
            this.removeEventListener('onEvent', onEventHandler);
            this.addEventListener('onEvent', onEventHandler, OSM.Events.eventHandlers.types.visualization);
            this.__onEventHandler__ = onEventHandler;
            var surveyID = survey.get("uniqueID");
            var uniqueID = this.get("uniqueID");
            var onAfterRenderHandler = function onAfterRenderHandler() {
                var svg = document.querySelector("#liSignatureContainer_" + uniqueID + " svg");
                if (svg) {
                    svg.addEventListener(RM.OSM.PlatformSpecifics.ontouchstartEventFormat.replace("on", ""), function(evt) {
                        RM.OSM.Renderers.signatureFunctions.downHandler(evt, surveyID, uniqueID);
                    }, false);
                    svg.addEventListener(RM.OSM.PlatformSpecifics.ontouchmoveEventFormat.replace("on", ""), function(evt) {
                        RM.OSM.Renderers.signatureFunctions.moveHandler(evt, uniqueID);
                    }, false);
                    svg.addEventListener(RM.OSM.PlatformSpecifics.ontouchendEventFormat.replace("on", ""), function(evt) {
                        RM.OSM.Renderers.signatureFunctions.upHandler(evt, surveyID, uniqueID);
                    }, false);
                    svg.addEventListener("mouseout", function(evt) {
                        if (evt.target.tagName.toLowerCase().match(/line|svg/) && !evt.relatedTarget.tagName.toLowerCase().match(/line|svg/)) {
                            RM.OSM.Renderers.signatureFunctions.upHandler(evt, surveyID, uniqueID);
                        }
                    }, false);
                }
            }; // this.removeEventListener('onAfterRender', onAfterRenderHandler);
            if (survey.get('jitRenderingEnabled')) {
                this.get("comment").onAfterRenderHandlers = [onAfterRenderHandler];
            } else {
                survey.addEventListener('onAfterRender', onAfterRenderHandler, OSM.Events.eventHandlers.types.visualization);
            }
            this.__onAfterRenderHandler__ = onAfterRenderHandler;
        }
        if (typeof RM.OSM.Renderers.signatureProperties === "undefined") {
            RM.OSM.Renderers.signatureProperties = {
                "version" : 180418,
            };
            RM.OSM.Renderers.signatureFunctions = {
                /**
                 * Check if the user event is a click or touch.
                 * @method isTouchEvent
                 * @author bivanov
                 * @private
                 * @param   {object} event  Event object
                 * @return  {boolean}       Returns true if it is a tousch event
                 */
                "isTouchEvent": function isTouchEvent(event) {
                    return event.type.match(/^touch/);
                },
                /**
                 * Returns the current coordinates of the cursor.
                 * @method getCoords
                 * @author bivanov
                 * @private
                 * @param   {object} event    Event object
                 * @return  {string}          Horizontal and vertical coordinates of cursor, separated by comma
                 */
                "getCoords": function getCoords(event) {
                    if (event.target.tagName.toLowerCase() === "svg") {
                        var svgElementCoords = event.target.getBoundingClientRect();
                        if (RM.OSM.Renderers.signatureFunctions.isTouchEvent(event)) {
                            return parseInt(event.targetTouches[0].clientX - svgElementCoords.left, 10) + ',' + parseInt(event.targetTouches[0].clientY - svgElementCoords.top, 10);
                        } else {
                            return parseInt(event.clientX - svgElementCoords.left, 10) + ',' + parseInt(event.clientY - svgElementCoords.top, 10);
                        }
                    } else {
                        return null;
                    }
                },
                /**
                 * Mousedown/touchstart event handler.
                 * @method downHandler
                 * @author bivanov
                 * @private
                 * @param   {object} event    Event object
                 * @param   {string} surveyID Unique ID of survey
                 * @param   {string} uniqueID Unique ID of question
                 */
                "downHandler": function downHandler(event, surveyID, uniqueID) {
                    var signatureProperties = RM.OSM.Renderers.signatureProperties[uniqueID];
                    if (signatureProperties.questionRef.get("isEnabled") !== false && !signatureProperties.questionRef.get("readOnly")) {
                        RM.OSM.Renderers.signatureFunctions.handleIScroll(surveyID, true);
                        if (RM.OSM.Renderers.signatureFunctions.isTouchEvent(event)) {
                            event.preventDefault();
                        }
                        signatureProperties.signPathString += 'M' + RM.OSM.Renderers.signatureFunctions.getCoords(event) + ' ';
                        RM.OSM.Renderers.signatureFunctions.getPathElement(uniqueID).setAttribute('d', signatureProperties.signPathString);
                        signatureProperties.isDown = true;
                    }
                },
                /**
                 * Mousemove/touchmove event handler.
                 * @method moveHandler
                 * @author bivanov
                 * @private
                 * @param   {object} event    Event object
                 * @param   {string} uniqueID Unique ID of question
                 */
                "moveHandler": function moveHandler(event, uniqueID) {
                    var signatureProperties = RM.OSM.Renderers.signatureProperties[uniqueID];
                    if (signatureProperties.questionRef.get("isEnabled") !== false && !signatureProperties.questionRef.get("readOnly")) {
                        if (RM.OSM.Renderers.signatureFunctions.isTouchEvent(event)) {
                            event.preventDefault();
                        }
                        var coords = RM.OSM.Renderers.signatureFunctions.getCoords(event);
                        if (signatureProperties.isDown && coords) {
                            signatureProperties.signPathString += 'L' + coords + ' ';
                            RM.OSM.Renderers.signatureFunctions.getPathElement(uniqueID).setAttribute('d', signatureProperties.signPathString);
                        }
                    }
                },
                /**
                 * Mouseup/touchend event handler.
                 * @method upHandler
                 * @author bivanov
                 * @private
                 * @param   {object} event    Event object
                 * @param   {string} surveyID Unique ID of survey
                 * @param   {string} uniqueID Unique ID of question
                 */
                "upHandler": function upHandler(event, surveyID, uniqueID) {
                    var signatureProperties = RM.OSM.Renderers.signatureProperties[uniqueID];
                    if (signatureProperties.questionRef.get("isEnabled") !== false && !signatureProperties.questionRef.get("readOnly")) {
                        RM.OSM.Renderers.signatureFunctions.handleIScroll(surveyID, false);
                        if (RM.OSM.Renderers.signatureFunctions.isTouchEvent(event)) {
                            event.preventDefault();
                        }
                        signatureProperties.isDown = false;
                        signatureProperties.questionRef.setComment(RM.OSM.Renderers.signatureFunctions.fromSVGtoOSM(signatureProperties.signPathString), RM.Utils.eventOriginType.User);
                    }
                },
                /**
                 * Sets a signature path to the svg canvas.
                 * @method setSignature
                 * @author bivanov
                 * @private
                 * @param   {string} uniqueID Unique ID of question
                 */
                "setSignature": function setSignature(uniqueID) {
                    var signatureProperties = RM.OSM.Renderers.signatureProperties[uniqueID];
                    RM.OSM.Renderers.signatureFunctions.getPathElement(uniqueID).setAttribute('d', signatureProperties.signPathString);
                },
                /**
                 * Clears the svg canvas.
                 * @method clearSignature
                 * @author bivanov
                 * @private
                 * @param   {string} uniqueID Unique ID of question
                 */
                "clearSignature": function clearSignature(uniqueID) {
                    var signatureProperties = RM.OSM.Renderers.signatureProperties[uniqueID];
                    if (signatureProperties.questionRef.get("isEnabled") !== false && !signatureProperties.questionRef.get("readOnly")) {
                        signatureProperties.signPathString = '';
                        RM.OSM.Renderers.signatureFunctions.getPathElement(uniqueID).setAttribute('d', '');
                    }
                },
                /**
                 * Returns the path element in the svg canvas.
                 * @method getPathElement
                 * @author bivanov
                 * @private
                 * @param   {string}        uniqueID    Unique ID of question
                 * @return  {HTMLElement}               The path element in the svg canvas
                 */
                "getPathElement": function getPathElement(uniqueID) {
                    if (!RM.OSM.Renderers.signatureProperties[uniqueID].signPathElement) {
                        RM.OSM.Renderers.signatureProperties[uniqueID].signPathElement = document.getElementById("signPath_" + uniqueID);
                    }
                    return RM.OSM.Renderers.signatureProperties[uniqueID].signPathElement;
                },
                /**
                 * Enable/disable scrolling action.
                 * @method handleIScroll
                 * @author bivanov
                 * @private
                 * @param   {string}    scrollID ID of scroll
                 * @param   {boolean}   disable  Flag fo eanbling/disabling
                 */
                "handleIScroll": function handleIScroll(scrollID, disable) {
                    var iScroll = RM.OSM.Renderers.getSlideScroll(scrollID);
                    if (iScroll) {
                        if (disable) {
                            iScroll.disable();
                        } else {
                            iScroll.enable();
                        }
                    }
                },
                /**
                 * Converts SVG coordinates to OSM coordinates.
                 * @method fromSVGtoOSM
                 * @author bivanov
                 * @private
                 * @param   {string} SVGcoords  SVG coordinates
                 * @return  {string}            OSM coordinates
                 */
                "fromSVGtoOSM": function fromSVGtoOSM(SVGcoords) {
                    if (SVGcoords === "") {
                        return "";
                    } else {
                        var OSMcoords = [];
                        SVGcoords = SVGcoords.replace(/\s/gim, "");
                        SVGcoords = SVGcoords.replace("M", "");
                        var paths = SVGcoords.split("M");
                        var temp = [];
                        for (var p in paths) {
                            temp = paths[p].split(/\,|L/gim);
                            temp = temp.map(function(element) {
                                return parseInt(element, 10);
                            });
                            OSMcoords.push(temp.join(","));
                        }
                        return OSMcoords.join("|");
                    }
                },
                /**
                 * Converts OSM coordinates to SVG coordinates.
                 * @method fromSVGtoOSM
                 * @author bivanov
                 * @private
                 * @param   {string} SVGcoords  OSM coordinates
                 * @return  {string}            SVG coordinates
                 */
                "fromOSMtoSVG": function fromOSMtoSVG(OSMcoords) {
                    if (OSMcoords === "") {
                        return "";
                    } else {
                        OSMcoords = OSMcoords.split("|");
                        var temp = [];
                        var SVGcoords = [];
                        var tempLen = 0;
                        for (var i in OSMcoords) {
                            temp = OSMcoords[i].split(",");
                            SVGcoords.push("M" + temp[0] + "," + temp[1]);
                            tempLen = temp.length;
                            for (var i = 2; i < tempLen; i++) {
                                if (i % 2 === 0) {
                                    SVGcoords.push("L" + temp[i] + "," + temp[i + 1]);
                                }
                            }
                        }
                        return SVGcoords.join(" ");
                    }
                },
                /**
                 * Converts a range expression string to an object.
                 * @method rengeExprToObject
                 * @author bivanov
                 * @private
                 * @param  {string} rangeString Range expression string
                 * @return {object|null}        Range expression object
                 */
                "rengeExprToObject" :function (rangeString) {
                    var nums = rangeString.split("..");
                    var min = parseInt(nums[0], 10);
                    var max = parseInt(nums[1], 10);
                    if (isNaN(min) || isNaN(max) || min > max) {
                        return null;
                    } else {
                        return { min, max };
                    }
                }
            };
        }
    },
    "model": function model(renderShellOnly) {
        var model = {};
        var templateEngine = RM.OSM.Renderers.TemplateEngine;
        var uniqueID = this.get("uniqueID");
        var jitRenderingProperties = RM.OSM.JITRenderingUtils.getJITRenderingProperties.call(this, renderShellOnly);
        var comment = this.get("comment");
        var answerSet = this.get('answerSet');
        var survey = this.getTop();
        var validator = this.get("validator");
        model["uniqueID"] = uniqueID;
        model["slideClass"] = RM.OSM.Renderers.getSurveyElementClassName.call(this, "ImageVisualization");
        model["renderShell"] = jitRenderingProperties["renderShell"];
        model["renderContent"] = jitRenderingProperties["renderContent"];
        // if (answerSet || !comment || this.get("dataType").get("length") < 32000) {
        var range = RM.OSM.Renderers.signatureFunctions.rengeExprToObject(validator.get("commentRange"));
        if (!range || range.max < 4000) {
            model["isWrongSetup"] = true;
            console.log(model);
            return model;
        }
        var path = RM.OSM.Renderers.signatureFunctions.fromOSMtoSVG(this.getComment());
        model["surveyID"] = survey.get('uniqueID');
        model["objectName"] = this.get('objectName');
        model["text"] = templateEngine.prepareElementForRender.call(this.get("text"));
        model["instructions"] = templateEngine.prepareElementForRender.call(this.get("instructions"), false, true, true);
        model["isRequired"] = validator.get("isRequired");
        model["path"] = path;
        model["userEventDown"] = RM.OSM.PlatformSpecifics.ontouchstartEventFormat;
        if (this.get("isEnabled") === false || this.get("readOnly")) {
            model["opacity"] = 0.3;
            model["buttonClass"] = "clrSignButton_disabled";
        } else {
            model["opacity"] = 1;
            model["buttonClass"] = "clrSignButton";
        }
        var screenSize = parseInt(Math.min(document.body.offsetWidth, document.body.offsetHeight), 10);
        var canvasWidth = screenSize - 100;
        var canvasHeight = parseInt(canvasWidth / 3, 10);
        var canvasPadding = parseInt(canvasWidth / 15, 10);
        model["size"] = {
            "canvasWidth": canvasWidth,
            "canvasHeight": canvasHeight,
            "linePoints": {
                "start": [canvasPadding, canvasHeight - canvasPadding],
                "end": [canvasWidth - canvasPadding, canvasHeight - canvasPadding]
            }
        };
        RM.OSM.Renderers.signatureProperties[uniqueID] = {
            "signPathString": path,
            "signPathElement": null,
            "questionRef": this,
            "isDown": false
        };
        return model;
    },
    "destroy": function destroy() {
        this.removeEventListener('onEvent', this.__onEventHandler__);
        this.getTop().removeEventListener('onAfterRender', this.__onAfterRenderHandler__);
        delete this.__onEventHandler__;
        delete this.__onAfterRenderHandler__;
        delete this.onAfterRenderHandlers;
        var uniqueID = this.get("uniqueID");
        delete RM.OSM.Renderers.signatureProperties[uniqueID];
        $("#slideLi_" + uniqueID).empty();
    },
    "style": (function() {
        var style = ".defaultSignatureTemplate_parentContainer {    text-align: center;    background-color: #E1E1E1;    padding: 40px;}.defaultSignatureTemplate_parentContainer .svgSignatureContainer {    display: inline-block;    background: #FFFFE4;    border: 2px solid #A6A6A6;    border-radius: 10px;}.defaultSignatureTemplate_parentContainer .clrSignButton_disabled {    display: inline-block;    padding: 10px;    margin-top: 20px;    border-radius: 0.3em;    font-weight: bold;    color: #2F2F2F;    background: #fafafa;    background: -moz-linear-gradient(top, #fafafa 0%, #ebebeb 100%);    background: -webkit-linear-gradient(top, #fafafa 0%,#ebebeb 100%);    background: linear-gradient(to bottom, #fafafa 0%,#ebebeb 100%);    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#fafafa', endColorstr='#ebebeb',GradientType=0 );}.defaultSignatureTemplate_parentContainer .clrSignButton {    display: inline-block;    padding: 10px;    margin-top: 20px;    border-radius: 0.3em;    cursor: pointer;    font-weight: bold;    color: #2F2F2F;    background: #fafafa;    background: -moz-linear-gradient(top, #fafafa 0%, #ebebeb 100%);    background: -webkit-linear-gradient(top, #fafafa 0%,#ebebeb 100%);    background: linear-gradient(to bottom, #fafafa 0%,#ebebeb 100%);    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#fafafa', endColorstr='#ebebeb',GradientType=0 );}";
        if (RM.OSM.PlatformSpecifics.ontouchstartEventFormat !== "ontouchstart") {
            style += ".defaultSignatureTemplate_parentContainer .clrSignButton:hover {        color: #FFFFFF;        background: #1ea5f4;        background: -moz-linear-gradient(top, #1ea5f4 0%, #077dd6 100%);        background: -webkit-linear-gradient(top, #1ea5f4 0%,#077dd6 100%);        background: linear-gradient(to bottom, #1ea5f4 0%,#077dd6 100%);        filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#1ea5f4', endColorstr='#077dd6',GradientType=0 );    }                    ";
        }
        return style;
    })()
});

OSM.Survey._Signature.set("template", "DefaultSignatureTemplate");
