OSM.Survey.addEventListener("onInit", function(event) {

    var arrDefaultQuestions = ["_Gender","_YearOfBirth","_Age","_AgeGroup","_Quota_City","_Quota_Gender","_Quota_AgeGroup","_Quota_Class","_Quota_LSM","_ProjectName","_SP1_Label","_SP2_Label","_Demographic_Infor","_Married","_Education","_Occupation","_Image_Product","_Image_House","_P30_ProductCode","_R5"];

    OSM.Survey.hideQuestions(arrDefaultQuestions);

    OSM.Survey.initializeQuestionComment(OSM.Survey._ProjectName, "2024 118 - CMI PICO SAPA");

    //--SECTION DISTRICTS 
    OSM.Survey.hideSectionWithQuestions(OSM.Survey._SEC_RESD_ADDRESS, ["_ResAddress","_ResDistricts","_ResDistrictSelected","_ResWards","_ResWardSelected"]);

    if(OSM.Survey.countCategories(OSM.Survey._ResProvinces.getAnswers()) == 0)
    {
        //Set all provinces
        OSM.Survey.initializeQuestion(OSM.Survey._ResProvinces, OSM.Survey.getArrayNumber(1, 63));

        //Set specific province
        /*
        var arr_provinces = ['_12','_22'];
        OSM.Survey.initializeQuestion(OSM.Survey._ResProvinces, arr_provinces);

        if(arr_provinces.length == 1)
        {
            OSM.Survey._ResDistricts.show();
            OSM.Survey._ResWards.show();
        }
        */

        OSM.Survey.initializeQuestion(OSM.Survey._Area, ['_1']);

        OSM.Survey.setInsert("itextvn", "");
        OSM.Survey.setInsert("str_note", "");
    }

    try 
    {
        for(var i = 1; i <= 30; i++)
        {
            OSM.Survey.setInsert("_DistrictLabel" + (i.toString().length == 1 ? "0" + i.toString() : i.toString()), "");
        }

        for(var i = 1; i <= 50; i++)
        {
            OSM.Survey.setInsert("_WardLabel" + (i.toString().length == 1 ? "0" + i.toString() : i.toString()), "");
        }
    }
    catch(e) {
        console.log(e);
    }
});
