RM.Utils.hideQuestionsOnInit(OSM.Survey, ['NWB_STATUS','NWB_LAST_SAVE_ON_SERVER','NWB_LAST_SUBMIT','NWB_CANCEL_REASON','SHELL_QFA','SHELL_QFB','SHELL_QFC','SHELL_QFD','SHELL_QFE','SHELL_QFF','System_Date','System_TimeIn','System_TimeOut','SHELL_START_DATE','SHELL_START_TIME','SHELL_INT_LENGTH','SHELL_BLOCK.SHELL_APPLICATION_ID','SHELL_BLOCK.SHELL_SCH1','SHELL_BLOCK.SHELL_SCH2','SHELL_BLOCK.SHELL_SCH3','SHELL_BLOCK.SHELL_GEOLOCATION_OUTCOME','SHELL_BLOCK.SHELL_GEOLOCATION_LATITUDE','SHELL_BLOCK.SHELL_GEOLOCATION_LONGITUDE','SHELL_BLOCK.SHELL_GEOLOCATION_ACCURACY','SHELL_BLOCK.SHELL_GEOLOCATION_TIMESTAMP','SHELL_CHAINID','SHELL_INFO_PROJECTSCRIPT_START','SHELL_DDG_STATUS','SHELL_TEL','SHELL_ADDRESS','SHELL_BLOCK_EMAIL.SHELL_EMAIL_DUMMY','_BHP','_PAQ']);

OSM.Survey.SHELL_RECORDING_CONFIRMATION.addEventListener("onNext", function(event) {

    if(OSM.Survey.SHELL_RECORDING_CONFIRMATION.isAnswerSelected(['_A1']))
    {
        /**********************************************
         * List of _Sampling
         * _1 : Phiếu Main     => set LoaiPhieu = '_1' 
         * _2 : Phiếu Booster  => set LoaiPhieu = '_5'
         * _3 : Phiếu Non      => set LoaiPhieu = '_6'
         **********************************************/

        if(OSM.Survey.countCategories(OSM.Survey._Sample.getAnswers()) == 0)
        {
            OSM.Survey.initializeQuestion(OSM.Survey._Sample, ['_1']);   
            OSM.Survey.initializeQuestion(OSM.Survey._Sampling, ['_2']); 
        }
    }
    else
    {
        OSM.Survey.get('navigator').goTo(OSM.Survey._ChamDut);
    }
});

OSM.Survey.SHELL_GENDER.addEventListener("onNext", function(event) {

    OSM.Survey.initializeQuestion(OSM.Survey._Gender, OSM.Survey.recodeByStep(OSM.Survey.SHELL_GENDER.getAnswers(), ["_A"], 0));

    //Quota _Gender
    OSM.Survey.initializeQuestion(OSM.Survey._Quota_Gender, OSM.Survey.recodeByStep(OSM.Survey.SHELL_GENDER.getAnswers(), ["_A"], 0));

    if(OSM.Survey.SHELL_GENDER.isAnswerSelected(['_A1']))
    {
       OSM.Survey.get('navigator').goTo(OSM.Survey._ChamDut);
    }
});

OSM.Survey.SHELL_AGE.addEventListener('onEntrance', function(e){ // start SHELL_AGE audio recording
    if (OSM.Survey.SHELL_RECORDING_CONFIRMATION.isAnswerSelected("_A1")) { //v7.72 - remove checking SHELL_GENDER
        OSM.Survey.startSilentAudioRecording("SHELL_AGE audio recording"); 
    }
});

OSM.Survey.SHELL_AGE.addEventListener('onNext', function(e){ //v7.77 - replace onExit to onNext     
    //v7.77 - move to same event
    if (OSM.Survey.SHELL_RECORDING_CONFIRMATION.isAnswerSelected("_A1")) { //v7.72 - remove checking SHELL_AGE
        OSM.Survey.stopSilentAudioRecording();
    }

    var objExactAge     = OSM.Survey.SHELL_AGE;
    var ageRecodedQuest = OSM.Survey.SHELL_AGE_RECODED;
    var exactAge = parseInt(objExactAge._A1.getComment());
      switch (true)
      {     //20160701 - adding an additional age band. 
            //20160713 - amending the additional age band to _A0 for continuity purpose.
           case (exactAge >= 10 && exactAge <= 15): ageRecodedQuest.setAnswers("_A0");
                                                    break;

          case (exactAge >= 16 && exactAge <= 24): ageRecodedQuest.setAnswers("_A1");
                                                   break;

          case (exactAge >= 25 && exactAge <= 34): ageRecodedQuest.setAnswers("_A2");
                                                   break;

          case (exactAge >= 35 && exactAge <= 44): ageRecodedQuest.setAnswers("_A3");
                                                   break;

          case (exactAge >= 45 && exactAge <= 54): ageRecodedQuest.setAnswers("_A4");
                                                   break;

          case (exactAge >= 55 && exactAge <= 64): ageRecodedQuest.setAnswers("_A5");
                                                   break;

          case (exactAge >= 65): ageRecodedQuest.setAnswers("_A6");
                                 break;

          default: ageRecodedQuest.setAnswers("_A7");  
      } 

    if(OSM.Survey.SHELL_AGE_RECODED.isAnswerSelected(['_A7']))
    {
        OSM.Survey.get('navigator').goTo(OSM.Survey._ChamDut);
    }
    else
    {

        if(exactAge >= 25 && exactAge <= 45)
        {
            var currentDate = new Date(OSM.Survey._STARTDATE.getComment());

            OSM.Survey._YearOfBirth.setComment(currentDate.getFullYear() - exactAge);
            OSM.Survey._Age.setComment(exactAge);

            var ageGroupRequest = OSM.Survey._AgeGroup;

            switch(true)
            {
                case (exactAge >= 25 && exactAge <= 35):
                    ageGroupRequest.setAnswers(['_2']);
                    break;    
                case (exactAge >= 36 && exactAge <= 45):
                    ageGroupRequest.setAnswers(['_3']);
                    break;
                default:
                    ageGroupRequest.setAnswers(['_98']);
            }

            //Quota DoTuoi
            OSM.Survey._Quota_AgeGroup.setAnswers(ageGroupRequest.getAnswers());
        }
        else
        {
            OSM.Survey.get('navigator').goTo(OSM.Survey._ChamDut);
        }
    }

    if (OSM.Survey.SHELL_RECORDING_CONFIRMATION.isAnswerSelected("_A1")) { //v7.72 - remove checking SHELL_AGE
        OSM.Survey.stopSilentAudioRecording();
    }

    // if(OSM.Survey.SHELL_RECORDING_CONFIRMATION.isAnswerSelected(['_A1']))
    // {
    //     var recordingNameDefault = 'ID' + OSM.Survey.SHELL_CHAINID.getComment();

    //     OSM.Survey.startSilentAudioRecording(recordingNameDefault); 
    // }  
});


OSM.Survey._ResName.addEventListener("onNext", function(event){

    OSM.Survey._ResDistricts.hide();
    OSM.Survey._ResWards.hide();

    if(OSM.Survey._ResDistricts.isAnswered()) OSM.Survey._ResDistricts.show();
    if(OSM.Survey._ResWards.isAnswered()) OSM.Survey._ResWards.show();
});

OSM.Survey._ResProvinces.addEventListener("onInputChange", function(event) {

    if(event.sourceElement.getAnswers().length != 0){

        var datasetResource = OSM.Survey.get('resourcesManager').getResource("DBSProvinces");

        if(datasetResource != undefined)
        {
            var tblProvinces = new RM.OSM.Survey.Resource.DataSetResource(datasetResource.get('objectGUID'));

            var sql_query = "ProvinceID==" + event.sourceElement[event.sourceElement.getAnswers()].get('objectName').replace('_','');

            tblProvinces.select("Districts", sql_query).then(function(data){

                console.log(data);

                if(data.length == 0){

                    OSM.Survey._SEC_RESD_ADDRESS.get('navigation').set('isAllowGoingForth', false);

                    OSM.NotificationSystem.notifyModal(OSM.Survey.objDBException.databaseSelectFailed);

                    return 0;
                }

                var row = data[0];

                objDistricts = JSON.parse('{' + row["Districts"] + '}');

                var arr_districts = [];

                for(const [key, value] of Object.keys(objDistricts).entries()){

                    OSM.Survey.setInsert("_DistrictLabel" + ((key + 1).toString().length == 1 ? "0" + (key + 1).toString() : (key + 1).toString()), objDistricts[value]);
                    arr_districts.push("_" + (key + 1).toString());
                }

                OSM.Survey._ResDistricts.show(); 

                OSM.Survey.initializeQuestion(OSM.Survey._ResDistricts, arr_districts);

                OSM.Survey.initializeQuestion(OSM.Survey._Quota_City, event.sourceElement.getAnswers());

                var province_id = event.sourceElement.get('answerSet')[event.sourceElement.getAnswers()].get('objectName');

                if(event.sourceElement.isAnswerSelected(['_1','_2'])){
                    OSM.Survey.setInsert("str_note", "<u>PVV lưu ý</u>: Cách nhập số điện thoại cố định ở " + areaCodes[province_id]['name'] + ": " + "0 + " + areaCodes[province_id]['area_code'] + " + XXXXXXXX (8 chữ số)");
                } else {
                    OSM.Survey.setInsert("str_note", "<u>PVV lưu ý</u>: Cách nhập số điện thoại cố định ở " + areaCodes[province_id]['name'] + ": " + "0 + " + areaCodes[province_id]['area_code'] + " + XXXXXXX (7 chữ số)");
                }
            }).catch(function(error){

                OSM.Survey._SEC_RESD_ADDRESS.get('navigation').set('isAllowGoingForth', false);

                OSM.NotificationSystem.notifyModal(OSM.Survey.objDBException.databaseNotAccessible);
            });
        }
        else
        {
            OSM.Survey._SEC_RESD_ADDRESS.get('navigation').set('isAllowGoingForth', false);

            OSM.NotificationSystem.notifyModal(OSM.Survey.objDBException.databaseNotFound);
        }
    }
});

OSM.Survey._ResDistricts.addEventListener("onInputChange", function(event) {

    if(event.sourceElement.getAnswers().length != 0){

        var datasetResource = OSM.Survey.get('resourcesManager').getResource("DBSDistricts");

        if(datasetResource != undefined)
        {
            var tblProvinces = new RM.OSM.Survey.Resource.DataSetResource(datasetResource.get('objectGUID'));

            var districtid = OSM.Survey._ResProvinces[OSM.Survey._ResProvinces.getAnswers()].get('objectName') + (event.sourceElement[event.sourceElement.getAnswers()].get('objectName').replace('_', '').length == 1 ? "0" : "") + event.sourceElement[event.sourceElement.getAnswers()].get('objectName').replace('_', '');

            var sql_query = "DistrictID=='" + districtid + "'";

            tblProvinces.select("Wards", sql_query).then(function(data){

                console.log(data);

                if(data.length == 0){

                    OSM.Survey._SEC_RESD_ADDRESS.get('navigation').set('isAllowGoingForth', false);

                    OSM.NotificationSystem.notifyModal(OSM.Survey.objDBException.databaseSelectFailed);

                    return 0;
                }

                var row = data[0];

                objWards = JSON.parse('{' + row["Wards"] + '}');

                var arr_wards = [];

                for(const [key, value] of Object.keys(objWards).entries()){

                    OSM.Survey.setInsert("_WardLabel" + ((key + 1).toString().length == 1 ? "0" + (key + 1).toString() : (key + 1).toString()), objWards[value]);
                    arr_wards.push("_" + (key + 1).toString());
                }

                OSM.Survey._ResWards.show(); 

                OSM.Survey.initializeQuestion(OSM.Survey._ResWards, arr_wards);

            }).catch(function(error){

                OSM.Survey._SEC_RESD_ADDRESS.get('navigation').set('isAllowGoingForth', false);

                OSM.NotificationSystem.notifyModal(OSM.Survey.objDBException.databaseNotAccessible);
            });
        }
        else
        {
            OSM.Survey._SEC_RESD_ADDRESS.get('navigation').set('isAllowGoingForth', false);

            OSM.NotificationSystem.notifyModal(OSM.Survey.objDBException.databaseNotFound);
        }
    }
});

OSM.Survey._ResWards.addEventListener("onInputChange", function(event) {

    if(event.sourceElement.isAnswered())
    {
        OSM.Survey._ResDistrictSelected.setComment(OSM.Survey._ResDistricts[OSM.Survey._ResDistricts.getAnswers()].get('text').getText());
        OSM.Survey._ResWardSelected.setComment(OSM.Survey._ResWards[OSM.Survey._ResWards.getAnswers()].get('text').getText());
    }
});

OSM.Survey._Sample.addEventListener("onNext", function(event){

    OSM.Survey.SHELL_NAME.setComment(OSM.Survey._ResName.getComment());

    if(OSM.Survey._ResPhone.isAnswerSelected(['_2']))
    {
        OSM.Survey.SHELL_BLOCK_TEL.SHELL_HOMETEL.setComment("0");
    }
    else
    {
        OSM.Survey.SHELL_BLOCK_TEL.SHELL_HOMETEL.setComment(OSM.Survey._ResPhone[OSM.Survey._ResPhone.getAnswers()].getComment());
    }

    if(OSM.Survey._ResCellPhone.isAnswerSelected(['_2']))
    {
        OSM.Survey.SHELL_BLOCK_TEL.SHELL_MOBTEL.setComment("0");
    }
    else
    {
        OSM.Survey.SHELL_BLOCK_TEL.SHELL_MOBTEL.setComment(OSM.Survey._ResCellPhone[OSM.Survey._ResCellPhone.getAnswers()].getComment());
    }

    //OSM.Survey.SHELL_ADDRESS.setComment(OSM.Survey._ResAddress.getComment() + ", " + OSM.Survey._ResDistrictSelected.getComment());

    OSM.Survey.SHELL_BLOCK_ADDRESS.SHELL_HOUSENO.setComment(OSM.Survey._ResHouseNo.getComment());
    OSM.Survey.SHELL_BLOCK_ADDRESS.SHELL_STREET.setComment(OSM.Survey._ResStreet.getComment());
    OSM.Survey.SHELL_BLOCK_ADDRESS.SHELL_DISTRICT.setComment(OSM.Survey._ResDistrictSelected.getComment());
    OSM.Survey.SHELL_BLOCK_ADDRESS.SHELL_TOWN.setComment(OSM.Survey._ResProvinces[OSM.Survey._ResProvinces.getAnswers()].get('text').getText());
    OSM.Survey.SHELL_BLOCK_ADDRESS.SHELL_ZIP.setComment(OSM.Survey._ResWardSelected.getComment());

    var str_address = OSM.Survey._ResHouseNo.getComment() + " " + OSM.Survey._ResStreet.getComment() + " " + OSM.Survey._ResWardSelected.getComment() + " " + OSM.Survey._ResDistrictSelected.getComment() + " " + OSM.Survey._ResProvinces[OSM.Survey._ResProvinces.getAnswers()].get('text').getText();

    OSM.Survey._ResAddress.setComment(str_address);
});

