OSM.Survey._LSM.addEventListener("onEntrance", function(event) {
     /*                         LSM                              *
     ************************************************************/

    //Go through all the iterations of loop LSM
    var _LSM = OSM.Survey._LSM;

    var numTotalScore = 0;

    _LSM.getIterator().forEach(function(iterator) {

        var iterationName = iterator.get('objectName');
        var iteration = _LSM.getIteration(iterationName);

        switch(iterationName)
        {
            case '_1': //Score of CC1
                iteration._LSM_Codes.setComment(OSM.Survey.getTotalMeasure(OSM.Survey._CC1));

                numTotalScore += parseInt(iteration._LSM_Codes.getComment());
                break;
            case '_2': //Score of CC2
                iteration._LSM_Codes.setComment(OSM.Survey.getTotalMeasure(OSM.Survey._CC2));

                numTotalScore += parseInt(iteration._LSM_Codes.getComment());
                break;
            case '_3': //Score of CC3
                iteration._LSM_Codes.setComment(OSM.Survey.getTotalMeasure(OSM.Survey._CC3));

                numTotalScore += parseInt(iteration._LSM_Codes.getComment());
                break;
            case '_4': //Score of CC4
                iteration._LSM_Codes.setComment(OSM.Survey.getTotalMeasure(OSM.Survey._CC4));

                numTotalScore += parseInt(iteration._LSM_Codes.getComment());
                break;
            case '_5': //Score of CC5
                var _CC5 = OSM.Survey._CC5;

                var index = -1;

                //Declare a variable to store the score of LSM
                var numScoreCC5 = 0;

                //Declare a variable to store the score if code TVs are selected
                var numScoreTV = 0;

                //Declare variables to store the quantity of the LCD and CRT television
                var numQuantityCode17 = 0, numQuantityCode18 = 0;

                //Go through all the iterations in loop CC5
                _CC5.getIterator().forEach(function (iteratorCC5) {
                    var iterationNameCC5 = iteratorCC5.get('objectName');
                    var iterationCC5 = _CC5.getIteration(iterationNameCC5);

                    var arrScoreCC5 = new Array();

                    //Score of CC5
                    switch(iterationNameCC5)
                    {
                        case '_1':
                            if(arrScoreCC5.length === 0) {
                                arrScoreCC5 = new Array(3,5,6,6,6);
                            }
                        case '_2':
                            if(arrScoreCC5.length === 0) {
                                arrScoreCC5 = new Array(0,2,3,4,5);
                            }
                        case '_3':
                            if(arrScoreCC5.length === 0) {
                                arrScoreCC5 = new Array(1,4,5,5,5);
                            }
                        case '_4':
                            if(arrScoreCC5.length === 0) {
                                arrScoreCC5 = new Array(3,5,7,7,7);
                            }
                        case '_5':
                            if(arrScoreCC5.length === 0) {
                                arrScoreCC5 = new Array(4,8,8,8,8);
                            }
                        case '_6':
                            if(arrScoreCC5.length === 0) {
                                arrScoreCC5 = new Array(4,7,7,7,7);
                            }
                        case '_7':
                            if(arrScoreCC5.length === 0) {
                                arrScoreCC5 = new Array(2,5,5,5,5);
                            }
                        case '_8':
                            if(arrScoreCC5.length === 0) {
                                arrScoreCC5 = new Array(4,7,7,7,7);
                            }
                        case '_9':
                            if(arrScoreCC5.length === 0) {
                                arrScoreCC5 = new Array(4,8,8,8,8);
                            }
                        case '_10':
                            if(arrScoreCC5.length === 0) {
                                arrScoreCC5 = new Array(4,9,9,9,9);
                            }
                        case '_11':
                            if(arrScoreCC5.length === 0) {
                                arrScoreCC5 = new Array(3,5,5,5,5);
                            }
                        case '_12':
                            if(arrScoreCC5.length === 0) {
                                arrScoreCC5 = new Array(3,5,6,6,6);
                            }
                        case '_13':
                            if(arrScoreCC5.length === 0) {
                                arrScoreCC5 = new Array(0,1,2,3,4);
                            }
                        case '_14':
                            if(arrScoreCC5.length === 0) {
                                arrScoreCC5 = new Array(2,3,4,4,4);
                            }
                        case '_15':
                            if(arrScoreCC5.length === 0) {
                                arrScoreCC5 = new Array(3,5,5,5,5);
                            }
                        case '_16':
                            if(arrScoreCC5.length === 0) {
                                arrScoreCC5 = new Array(0,0,0,0,0);
                            }

                            if(parseInt(iterationCC5._CC5_Codes.getComment()) > 0)
                            {
                                if(iterationCC5._CC5_Codes.getComment() >= 5)
                                {
                                    index = 4;
                                }
                                else
                                {
                                    index = parseInt(iterationCC5._CC5_Codes.getComment()) - 1;
                                }

                                numScoreCC5 += arrScoreCC5[index];
                            }
                            break;
                        case '_17':
                            numQuantityCode17 = parseInt(iterationCC5._CC5_Codes.getComment());
                        case '_18':
                            if(iterationNameCC5 === '_18') {
                                numQuantityCode18 = parseInt(iterationCC5._CC5_Codes.getComment());

                                if(numQuantityCode17 === 0 && numQuantityCode18 === 0)
                                {
                                    numScoreTV = 0; //Không có TV màu
                                }
                                else if(numQuantityCode17 === 1 && numQuantityCode18 === 0)
                                {
                                    numScoreTV = 2; //1 TV màu thường
                                }
                                else if(numQuantityCode17 > 1 && numQuantityCode18 === 0)
                                {
                                    numScoreTV = 4; //Từ 2 TV màu thường trở lên (toàn bộ là TV màu thường)
                                }
                                else if(numQuantityCode17 === 0 && numQuantityCode18 === 1)
                                {
                                    numScoreTV = 4; //1 TV LCD/ Plasma màu
                                }
                                else if(numQuantityCode17 > 0 && numQuantityCode18 > 0)
                                {
                                    numScoreTV = 6; //2 hoặc nhiều hơn 2 TV màu (trong đó có ít nhất 1 TV LCD/ Plasma)
                                }
                                else if(numQuantityCode17 === 0 && numQuantityCode18 > 1)
                                {
                                    numScoreTV = 6; //2 hoặc nhiều hơn 2 TV màu (trong đó có ít nhất 1 TV LCD/ Plasma)
                                }

                                numScoreCC5 += numScoreTV;
                            }
                            break;
                    }
                });

                iteration._LSM_Codes.setComment(numScoreCC5);

                numTotalScore += parseInt(iteration._LSM_Codes.getComment());
                break;
            case '_6':
                iteration._LSM_Codes.setComment(OSM.Survey.getTotalMeasure(OSM.Survey._CC6));

                numTotalScore += parseInt(iteration._LSM_Codes.getComment());
                break;
            case '_7':
                iteration._LSM_Codes.setComment(numTotalScore);
                break;
            case '_8':
                numTotalScore = numTotalScore * 10;

                switch(true)
                {
                    case (numTotalScore >= 1 && numTotalScore <= 75):
                        iteration._LSM_Codes.setComment(0);
                        break;
                    case (numTotalScore >= 76 && numTotalScore <= 130):
                        iteration._LSM_Codes.setComment(1);
                        break;
                    case (numTotalScore >= 131 && numTotalScore <= 175):
                        iteration._LSM_Codes.setComment(2);
                        break;
                    case (numTotalScore >= 176 && numTotalScore <= 215):
                        iteration._LSM_Codes.setComment(3);
                        break;
                    case (numTotalScore >= 216 && numTotalScore <= 260):
                        iteration._LSM_Codes.setComment(4);
                        break;
                    case (numTotalScore >= 261 && numTotalScore <= 305):
                        iteration._LSM_Codes.setComment(5);
                        break;
                    case (numTotalScore >= 306 && numTotalScore <= 350):
                        iteration._LSM_Codes.setComment(6);
                        break;
                    case (numTotalScore >= 351 && numTotalScore <= 400):
                        iteration._LSM_Codes.setComment(7);
                        break;
                    case (numTotalScore >= 401 && numTotalScore <= 455):
                        iteration._LSM_Codes.setComment(8);
                        break;
                    case (numTotalScore >= 456 && numTotalScore <= 520):
                        iteration._LSM_Codes.setComment(9);
                        break;
                    case (numTotalScore >= 521 && numTotalScore <= 595):
                        iteration._LSM_Codes.setComment(10);
                        break;
                    case (numTotalScore >= 596 && numTotalScore <= 675):
                        iteration._LSM_Codes.setComment(11);
                        break;
                    case (numTotalScore >= 676 && numTotalScore <= 770):
                        iteration._LSM_Codes.setComment(12);
                        break;
                    case (numTotalScore >= 771 && numTotalScore <= 875):
                        iteration._LSM_Codes.setComment(13);
                        break;
                    case (numTotalScore >= 876 && numTotalScore <= 990):
                        iteration._LSM_Codes.setComment(14);
                        break;
                    case (numTotalScore >= 991 && numTotalScore <= 1125):
                        iteration._LSM_Codes.setComment(15);
                        break;
                    case (numTotalScore >= 1126 && numTotalScore <= 1275):
                        iteration._LSM_Codes.setComment(16);
                        break;
                    case (numTotalScore >= 1276 && numTotalScore <= 1445):
                        iteration._LSM_Codes.setComment(17);
                        break;
                    case (numTotalScore >= 1446 && numTotalScore <= 1850):
                        iteration._LSM_Codes.setComment(18);
                        break;
                    case (numTotalScore >= 1851):
                        iteration._LSM_Codes.setComment(19);
                        break;
                }
                break;
        }

        iteration._LSM_Codes.disableComment();
    });
});

OSM.Survey._LSM.addEventListener("onExit", function(event){

    var LSM_Score = parseInt(OSM.Survey._LSM.getIteration('_8')._LSM_Codes.getComment());

    if(LSM_Score < 7)
    {
        OSM.Survey.get('navigator').goTo(OSM.Survey._ChamDut);  
    }
    else
    {
        switch(true)
        {
            case (LSM_Score >= 7 && LSM_Score <= 9):
                OSM.Survey.initializeQuestion(OSM.Survey._Quota_LSM, ['_1']);
                break;
            case (LSM_Score >= 10):
                OSM.Survey.initializeQuestion(OSM.Survey._Quota_LSM, ['_2']);
                break;    
        }
    }
});

