        OSM.Survey.get('navigator').goTo(OSM.Survey._ChamDut);
    }
});

OSM.Survey.addEventListenersToElements(["_R6a","_R6b","_R6c"], "onNext", function(event){

    var iteration = event.sourceElement.parent();
    var q_confirm_2 = undefined;

    switch(event.sourceElement.get('objectName'))
    {
        case "_R6a":
            q_confirm_2 = iteration._R6a_Reconfirm_2;
            break;
        case "_R6b":
            q_confirm_2 = iteration._R6b_Reconfirm_2;
            break;
        case "_R6c":
            q_confirm_2 = iteration._R6c_Reconfirm_2;
            break;
    }

    q_confirm_2.hide();

    switch(event.sourceElement.getAnswers())
    {
        case '_1':
            q_confirm_2.show();
            OSM.Survey.setInsert("moreorless", "tăng");
            OSM.Survey.setInsert("yesorno", "Có");
            OSM.Survey.setInsert("amount", "Tăng nhiều");
            break;
        case '_2':
            q_confirm_2.show();
            OSM.Survey.setInsert("moreorless", "tăng");
            OSM.Survey.setInsert("yesorno", "Có");
            OSM.Survey.setInsert("amount", "Tăng 1 chút");
            break;
        case '_3':
            OSM.Survey.setInsert("moreorless", "tăng hay giảm");
            OSM.Survey.setInsert("yesorno", "Không");
            break;
        case '_4':
            q_confirm_2.show();
            OSM.Survey.setInsert("moreorless", "giảm");
            OSM.Survey.setInsert("yesorno", "Có");
            OSM.Survey.setInsert("amount", "Giảm 1 chút");
            break;
        case '_5':
            q_confirm_2.show();
            OSM.Survey.setInsert("moreorless", "giảm");
            OSM.Survey.setInsert("yesorno", "Có");
            OSM.Survey.setInsert("amount", "Giảm nhiều");
            break;
    }
});

OSM.Survey.addEventListenersToElements(["_closing_note_p1"], "onBeforeAsk", function(s, q){

    var iteration = q.parent();

    q.hide();

    if(iteration.get('objectName') == "_1")
    {
        q.show();
    }
});

