OSM.Survey._Rotation_ID.addEventListener("onNext", function(event){

    OSM.Survey.initializeQuestion(OSM.Survey._Rotation, obj_Rotation[parseInt(event.sourceElement.getComment())]);
});

OSM.Survey._Rotation.addEventListener("onNext", function(event){


    switch(event.sourceElement.getAnswers())
    {
        case '_1':
            OSM.Survey.initializeQuestion(OSM.Survey._Phase.getIteration('_1')._ProductSelected, ['_1']);
            OSM.Survey.initializeQuestion(OSM.Survey._Phase.getIteration('_2')._ProductSelected, ['_2']);
            break;
        case '_2':
            OSM.Survey.initializeQuestion(OSM.Survey._Phase.getIteration('_1')._ProductSelected, ['_2']);
            OSM.Survey.initializeQuestion(OSM.Survey._Phase.getIteration('_2')._ProductSelected, ['_1']);
            break;
    }
    debugger;
    OSM.Survey._Rotation_Loop.getIteration('_1')._Rotation_Codes['_1'].enable();
    OSM.Survey._Rotation_Loop.getIteration('_1')._Rotation_Codes['_2'].enable();
    OSM.Survey._Rotation_Loop.getIteration('_2')._Rotation_Codes['_1'].enable();
    OSM.Survey._Rotation_Loop.getIteration('_2')._Rotation_Codes['_2'].enable();

    OSM.Survey._Rotation_Loop.getIteration('_1')._Rotation_Codes.setAnswers('_1');
    OSM.Survey._Rotation_Loop.getIteration('_2')._Rotation_Codes.setAnswers('_2');

    OSM.Survey._Rotation_Loop.getIteration('_1')._Rotation_Codes['_2'].disable();
    OSM.Survey._Rotation_Loop.getIteration('_2')._Rotation_Codes['_1'].disable();

    OSM.Survey._SP1_Label.setComment(OSM.Survey._Phase.getIteration('_1')._ProductSelected[OSM.Survey._Phase.getIteration('_1')._ProductSelected.getAnswers()].get('text').getText());
    OSM.Survey._SP2_Label.setComment(OSM.Survey._Phase.getIteration('_2')._ProductSelected[OSM.Survey._Phase.getIteration('_2')._ProductSelected.getAnswers()].get('text').getText());
});

OSM.Survey._P30.addEventListener("onNext", function(event){

    OSM.Survey._P31_Text.hide();

    if(!event.sourceElement.isAnswerSelected(['_3']))
    {
        OSM.Survey._P31_Text.show();
        OSM.Survey.initializeQuestion(OSM.Survey._P30_ProductCode, OSM.Survey._Phase.getIteration(event.sourceElement.getAnswers())._ProductSelected.getAnswers());
    }
});

OSM.Survey._D5_1.addEventListener("onNext", function(event){

    OSM.Survey.initializeQuestion(OSM.Survey._D5_2, event.sourceElement.getAnswers());
});

OSM.Survey._Confirmed.addEventListener("onEntrance", function(event){

    OSM.Survey.stopSilentAudioRecording();

    if(OSM.Survey.SHELL_RECORDING_CONFIRMATION.isAnswerSelected(['_A1']))
    {
        var recordingNameDefault = 'ID' + OSM.Survey.SHELL_CHAINID.getComment() + event.sourceElement.get('objectName');

        OSM.Survey.startSilentAudioRecording(recordingNameDefault); 
    } 
});

OSM.Survey._Confirmed.addEventListener("onNext", function(event){

    OSM.Survey.stopSilentAudioRecording();

    if(event.sourceElement.isAnswerSelected(['_2'])){

        OSM.Survey.get('navigator').goTo(OSM.Survey._ChamDut);
    }
});

OSM.Survey._Info_Sup.addEventListener("onNext", function(event){

    OSM.Survey._Image_Sup.hide();

    if(event.sourceElement.isAnswerSelected(['_1']))
    {
        OSM.Survey._Image_Sup.show();
    }
});

OSM.Survey.addEventListenersToElements(["_R1","_R2a_Text","_R2b_Text","_R5","_R5_NEW","_R7"], "onEntrance", function(event){

    OSM.Survey.stopSilentAudioRecording();

    var iteration = event.sourceElement.parent().get('objectName');

    if(OSM.Survey.SHELL_RECORDING_CONFIRMATION.isAnswerSelected(['_A1']))
    {
        var recordingNameDefault = 'ID' + OSM.Survey.SHELL_CHAINID.getComment() + event.sourceElement.get('objectName') + "_SP" + iteration;

        OSM.Survey.startSilentAudioRecording(recordingNameDefault); 
    } 
}); 

