OSM.Survey._KetThuc.addEventListener("onNext", function(event){

    //SET LOAIPHIEU = MAIN => PHIEU COMPLETED
    OSM.Survey._LoaiPhieu.hideAnswers();

    var respSampling = OSM.Survey._Sample.getAnswers();

    switch(respSampling)
    {
        case '_1':
            OSM.Survey._LoaiPhieu.setAnswers(['_1']);
            OSM.Survey._LoaiPhieu.showAnswers(['_1']);

            OSM.Survey.NWB_STATUS.setAnswers(['_2']);
            break;
        case '_2':
            OSM.Survey._LoaiPhieu.setAnswers(['_5']);
            OSM.Survey._LoaiPhieu.showAnswers(['_5']);

            OSM.Survey.NWB_STATUS.setAnswers(['_2']);
            break;
        case '_3':
            OSM.Survey._LoaiPhieu.setAnswers(['_6']);
            OSM.Survey._LoaiPhieu.showAnswers(['_6']);

            OSM.Survey.NWB_STATUS.setAnswers(['_2']);
            break;
    }

    OSM.Survey.get('navigator').goTo(OSM.Survey._LoaiPhieu);
});

OSM.Survey._ChamDut.addEventListener("onNext", function(event) {
    //SET LOAIPHIEU = CANCEL => PHIẾU UNCOMPLETED OR PHIẾU KHÔNG THỎA ĐIỀU KIỆN PHỎNG VẤN
    OSM.Survey._LoaiPhieu.hideAnswers();
    OSM.Survey._LoaiPhieu.setAnswers(['_3']);
    OSM.Survey._LoaiPhieu.showAnswers(['_3']);

    OSM.Survey.NWB_STATUS.setAnswers(['_3']);
    //OSM.Survey.get('navigator').goTo(OSM.Survey._LoaiPhieu); 
});

OSM.Survey._LoaiPhieu.addEventListener("onNext", function (event){

    //SET QUOTAS
    if(OSM.Survey._LoaiPhieu.isAnswerSelected(['_3'])){

        OSM.Survey._Quota_City.clearAnswers();
        OSM.Survey._Quota_Gender.clearAnswers();
        OSM.Survey._Quota_AgeGroup.clearAnswers();
        OSM.Survey._Quota_Class.clearAnswers();
        OSM.Survey._Quota_LSM.clearAnswers();
    } 

    var endInterviewDate = new Date();

    var prevenddate = OSM.Survey._ENDDATE.getComment();

    if(prevenddate === '')
    {
        OSM.Survey._ENDDATE.enableComment();
        OSM.Survey._ENDDATE.setComment(OSM.Survey.getMyDate(endInterviewDate, "YYYY-MM-DD"));
        OSM.Survey._ENDDATE.disableComment();
    }

    var prevendtime = OSM.Survey._ENDTIME.getComment();

    if(prevendtime === '')
    {
        OSM.Survey._ENDTIME.enableComment();
        OSM.Survey._ENDTIME.setComment(OSM.Survey.getMyTime(endInterviewDate));
        OSM.Survey._ENDTIME.disableComment();

        //alert(OSM.Survey.spandate("2016-10-11","2016-10-12","00:10:59","23:15:30"));

        var str_spend_date = OSM.Survey.spendDate(OSM.Survey._STARTDATE.getComment(), OSM.Survey._ENDDATE.getComment(), OSM.Survey._STARTTIME.getComment(), OSM.Survey._ENDTIME.getComment())

        OSM.Survey._SPANTIME.enableComment();
        OSM.Survey._SPANTIME.setComment(str_spend_date);
        OSM.Survey._SPANTIME.disableComment();

        OSM.Survey._TOTALTIME.enableComment();
        OSM.Survey._TOTALTIME.setComment(OSM.Survey.totalTime(str_spend_date));
        OSM.Survey._TOTALTIME.disableComment();
    }
});

this.getTop().hintShellAge = function(question, survey){

    var v = 10;
    v = Math.floor(Math.random() * 99) + 10;

    var hintObject = {
        "answers" : ['_A1'],
        "other" : {
            '_A1' : v
        }
    }
    return hintObject;
}

