this.getTop().hintQuestions = function(question, survey){
    debugger;
    var values = [];
    var v = "";

    var hintObject = {}

    var objdataType = question.get('dataType');

    switch(objdataType.get('datatype')){
        case 1: //Category
            switch(question.get('objectName')){
                case '_ResPhone':
                    break;
                case 'SHELL_AGE':
                    hintObject["answers"] = ["_A1"];

                    if(hintObject["other"] == undefined) hintObject["other"] = {};

                    hintObject["other"]["_A1"] = Math.floor(Math.random() * 99) + 10;
                    break;
                case '_ResCellPhone':
                    var values = ["0972111111","0972222222","0973333333"];
                    var i = Math.floor(Math.random() * values.length);

                    hintObject["answers"] = ["_1"];

                    if(hintObject["other"] == undefined) hintObject["other"] = {};

                    hintObject["other"]["_1"] = values[i]
                    break;
            }
            break;
        case 2: //Numeric
            if(hintObject["comment"] === undefined){
                hintObject["comment"] = 0
            }  

            hintObject["comment"] = Math.floor(Math.random() * 2147483647) + (-2147483648);
            break;
        case 3: //Text
            if(hintObject["comment"] === undefined){
                hintObject["comment"] = 0
            }

            var l = question.get('datatype').get('length');
            var lr = (Math.random() * l) + 7;
            var str = (Math.random() + 1).toString(36).substring(lr);

            hintObject["comment"] = str;
            break;      
        case 4: //DateTime
            //questionRef.enableComment();
            break;
    }

    return hintObject;
}
